css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theverge.com") {
    body
    , .c-global-header.above::before
    {
        background: #3d3d3d !important;
        color: #EEEEEE !important;
    }
    
    .above .c-global-header__logo a {
        background-color: #FA4B2A !important;
    }
    
    #container
    , .modal_wrapper
    , .p-entry-nav
    , .m-big-stories__main 
    , .l-wrapper
    , .l-segment 
    , .l-main-content
	, .l-hub-wrapper
    {
        background: #2d2d2d !important;
    }
    
    article, .m-entry-section, .m-big-stories__toggle, .m-big-stories__dropdown, .m-big-stories__story {
        background: #2d2d2d !important;
        color: white !important;
    }
    
    .m-big-stories__toggle::before {
        border-color: white transparent !important;
    }
    
    .widget.trending-dicussions {
        background: #2d2d2d !important;
    }
    
    #network-bar, .storybox, .story, .pagination, header nav, header nav #hubs-nav, header nav #topics-nav, #nav-right, .m-carousel, .m-carousel h3, .m-entry-section__more {
        background: #2d2d2d !important;
    }
    
    .wf-active .m-entry-section__more
    {
        background: #2d2d2d !important;
    }
    
    .hub-chunk {
        background: #2d2d2d !important;
    }
    
    a:not(.m-carousel__stream-header) {
        background: inherit !important;
        /*color: #e5127d !important;*/
    }
    
    h6 a {
        color: inherit !important;
    }
    
    p, q {
        color: white !important;
    }
    
    .footnote p {
        border-top: 1px solid #AAAAAA !important;
    }
    
    .breaker a.previous.disabled:hover, .breaker a.next.disabled:hover,
    .breaker a.previous.enabled:hover, .breaker a.next.enabled:hover
    {
        background: #4d4d4d !important;
    }
    
    .breaker a.previous.disabled, .breaker a.next.disabled,
    .breaker a.previous.enabled, .breaker a.next.enabled{
        background: #3d3d3d !important;
    }
    
    .comments-header, article.permalink blockquote {
        background: #4d4d4d !important;
    }
    
    h1, h2, h3, h4, h5, h6, .label,
    .story-navigation ul .column a span,
    .story-navigation ul .column a strong{
        color: white !important;
    }
    
    .social-media-column {
    	visibility:hidden !important;
    }
    
    #ZRa, .pgh-link  {
    	visibility:hidden !important;
    }
    
    .v9 {
        height: 0px !important;
        width: 0px !important;
    	visibility:hidden !important;
    }
    
    article li {
        color: white !important;
    }
    
    #main_comment_form, .cform, .citem:Not(.recommended) {
        background: #2d2d2d !important;
    }
    
    #network-bar .user.logged-in .dropdown {
        background: #2d2d2d !important;
    }
    
    
    .c-comments__post-form {
        background: #2d2d2d !important;
        color: white !important;
        border: 2px solid #7d7d7d;
        margin-top: 25px;
    }
    
    .c-comments__form-button svg
    , .c-comments__form-button
    , .c-comments__form-button svg use
    , .p-svg-icon{
        fill: white !important;
    }
    
    .cform textarea
    , .markItUpEditor
    , .c-comments__post-form textarea
    , .c-comments__message
    {
        background: #3D3D3D !important;
        color: white !important;
    }
    
     .m-timn__comment-blurb
    {
        background: #2d2d2d !important;
    }
    
    blockquote {
        background: #4d4d4d !important;
        color: white !important;
    }
    
    .-previous, .-next {
        background: #3D3D3D !important;
    }
     
    .-previous.is-active:hover, .-next.is-active:hover {
        background: #4d4d4d !important;
    }
    
    .-previous.is-active:hover::after, .-next.is-active:hover::after {
        text-shadow: 0 0 3px white;
    }
    
    .-previous:not(.is-active)::after, .-next:not(.is-active)::after {
        color: #7d7d7d !important;
    }
    
    .is-active::after
    , .c-comments__post-actions button
    {
         color: #EEEEEE !important;
     }
    
    .c-comments__list {
        border-top: 1px solid #7d7d7d;
    }
    
    .c-comments__comment {
        border-top: 1px solid #7d7d7d;
        border-bottom: 1px solid #7d7d7d;
        border-left: 3px solid #7d7d7d;
    }
    
    /*
    #read-this-now, #recent-games-reviews, #biggest-stories {
        border: 6px #6d6d6d !important;
    }
    */

    .c-tab-bar {
        display: none !important;
    }
    
    
    /* Verge 3.0 Starts here!*/
    .c-comments__recommended {
        background: #3D3D3D !important;
    }
    
    .c-comments__author-title {
        color: #E5127D !important;
        font-weight: bold !important;
    }
    
    .c-comments__count span {
        color: #E5127D !important;
    }
    
    /*
    .c-comments__folder author {
        opacity: 1 !important;
    }
    */
    
    .c-comments__folder {
        opacity: 1 !important;
        color: #EEEEEE !important;
    }
    
    /*
    .c-comments__cbody blockquote {
        border-top: 1px solid #e5127d;
        border-bottom: 1px solid #e5127d;
    }
    */
    
    .c-comments__post-actions [class^="icon-"]::before {
        color: #E5127D !important;
    }
    
    c-comments__settings-button {
        font-weight: bold !important;
    }
    
    .c-hub-entry--article:Not(.c-hub-entry--hero),
    .c-hub-entry:Not(.c-hub-entry--hero) {
        background: #2D2D2D !important;
    }
	
	.c-compact-river__entry {
		background-color: transparent !important;
		border-top-color: #8d8d8d !important;
	}
    
    .c-hub-entry__title,
    .c-hub-entry__title a 
    .c-hub-entry__title h2 a
    , .p-entry-header__labels a
    , .p-entry-header__byline .email a, .p-entry-header__byline .twitter a{
        color: #FEFEFE !important;
    }
    
    .c-hub-entry--feature:Not(.c-hub-entry--hero),
    .c-hub-entry--linkpost:Not(.c-hub-entry--hero) {
        background: #4B0629 !important;
    }
    
    .c-pagination__wrap a {
        background: #4d4d4d !important;
    }
	
	.c-global-header,
	.c-global-header inline,
    .c-global-header::before,
    .c-global-header .l-wrapper,
    .c-global-header__links,
    .c-seven-up
	{
		background: #1d1d1d !important;
	}
    
    .c-seven-up__main .c-hub-entry--hero:not(:last-child) {
        background: #1d1d1d !important;
    }
    
    .EmbeddedTweet-tweet,
    .EmbeddedTweet,
    .Tweet,
    .Tweet-body{
        background-color: #2D2D2D !important;
        color: white !important;
    }
    
    .u-desktop-only {
        display: none !important;
    }
    
    .c-entry-intro {
        font-size: 18px;
    }
    
    .m-timn__prices
    , .m-timn__wrap-up__card .photo
    , .p-review-score
    , .photo
    , #js-timn-runner-up-card
    , #js-timn-winner-card
    {
        background: #4d4d4d !important;
    }
    
    .c-comments__cbody {
        font-size: 17px !important;
    }
    
    .p-modal__window {
        background-color: #3d3d3d !important;
        color: #EFEFEF !important;
    }
    
    .c-auth__main-tabs li.is-active {
        background-color: #5d5d5d !important;
    }
    
    .c-auth__main-tabs ul 
    , .c-auth__main-tabs li.is-active
    {
        border-color: #9d9d9d !important;
    }

    .p-text-input
    {
        background-color: #6d6d6d !important;
        color: #EFEFEF !important;
    }
    
    .p-input-header {
        background-color: #4d4d4d !important;
    }
    
    .p-input-header__input {
        color: #EFEFEF !important;
    }
    
    .c-entry-group-labels__item::before {
	   display: none;
    }   
    
    .c-entry-group-labels__item {
        color: #EFEFEF !important;
    }
    
    .c-entry-sidebar {
        background-color: #3D3D3D !important;
    }
 
    .c-entry-sidebar li {
        color: #EFEFEF !important;
    }
    
    .e-image__meta {
        color: #EFEFEF !important;
    }
    
    #comments-touch-tools {
        background-color: #4d4d4d !important;
        opacity: .8 !important;   
    }
    
    .c-comments__touch-tools button {
        color: #EFEFEF !important;
    }
}
