css:
@-moz-document domain(codeanywhere.com){
  	div.layout-fit div.multisplit-content-wrapper .active.visible div.CodeMirror div.cm-keyword,
    div.layout-fit div.multisplit-content-wrapper .visible.active div.CodeMirror div.cm-atom,
    div.layout-fit div.multisplit-content-wrapper .visible.active div.CodeMirror div.cm-number,
    div.layout-fit div.multisplit-content-wrapper .active.visible div.CodeMirror div.cm-variable-3 {
      color: #f6acff !important;
      text-shadow: 1px 1px 8px #b000e6, 1px -1px 8px #b000e6, -1px 1px 8px #b000e6, -1px -1px 8px #b000e6 !important;
    }
    .layout-fit .multisplit-content-wrapper visible.active .CodeMirror .cm-def,
    .layout-fit .multisplit-content-wrapper .active.visible .active.visible .CodeMirror .cm-tag,
    .layout-fit .multisplit-content-wrapper visible.active .CodeMirror .cm-meta,
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-qualifier {
      color: #fdb87d !important;
      text-shadow: 1px 1px 8px #9d3c00, 1px -1px 8px #9d3c00, -1px 1px 8px #9d3c00, -1px -1px 8px #9d3c00 !important;
    }
    .multisplit-content-wrapper .active.visible .CodeMirror .cm-variable {
      color: #ffffa9 !important;
      text-shadow: 1px 1px 8px #777200, 1px -1px 8px #777200, -1px 1px 8px #777200, -1px -1px 8px #777200 !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-variable-2 {
      color: #9cddfa !important;
      text-shadow: 1px 1px 8px #000afd, 1px -1px 8px #000afd, -1px 1px 8px #000afd, -1px -1px 8px #000afd !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-property,
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-attribute {
      color: #85f0cb !important;
      text-shadow: 1px 1px 8px #008b26, 1px -1px 8px #008b26, -1px 1px 8px #008b26, -1px -1px 8px #008b26 !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-global-variable {
      color: #ff3e3e !important;
      text-shadow: 1px 1px 8px #a30000, 1px -1px 8px #a30000, -1px 1px 8px #a30000, -1px -1px 8px #a30000 !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-string {
      color: #d0a74c;
    }
    .multisplit-content-wrapper .active.visible .active.visible .CodeMirror .cm-string-2 {
      color: #fd55d7;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-operator {
      color: #dac1fd;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .cm-comment {
      color: #989898;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror,
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .CodeMirror-scroll {
      background-color: #050505;
      color: #dac1fd;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .CodeMirror-selected {
      background: rgba(2, 56, 158, 0.5);
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror-focused .CodeMirror-selected {
      background: rgba(61, 127, 252, 0.5);
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .CodeMirror-gutters {
      background-color: #050505;
      border-right: 1px solid #5c4976;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror-activeline .CodeMirror-gutter-elt {
      color: #dac1fd !important;
      text-shadow: 1px 1px 8px #720dff, 1px -1px 8px #720dff, -1px 1px 8px #720dff, -1px -1px 8px #720dff !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror-gutter-elt {
      color: #5c4976 !important;
    }
    .multisplit-content-wrapper .CodeMirror .active.visible .CodeMirror-matchingbracket {
      background-color: #dac1fd;
      color: #050505 !important;
      box-shadow: 1px 1px 8px #720dff, 1px -1px 8px #720dff, -1px 1px 8px #720dff, -1px -1px 8px #720dff !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror-matchingtag {
      background: #8542e3;
    }
    .multisplit-content-wrapper .CodeMirror-matchingtag.cm-string {
      color: #e5cf9d;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror-activeline-background,
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror-activeline .CodeMirror-gutter-elt {
      background: #292929 !important;
    }
    .multisplit-content-wrapper .CodeMirror .active.visible .CodeMirror-cursor {
      border-left: 1px solid #dac1fd !important;
    }
    .layout-fit .multisplit-content-wrapper .active.visible .CodeMirror .CodeMirror-overwrite .CodeMirror-cursor {
      border-left: none !important;
      border-bottom: 1px solid #dac1fd;
      width: 1.2ex;
    }
    .layout-fit .multisplit-content-wrapper #image-holder,
    .layout-fit .multisplit-content-wrapper #not-editor {
      background-color: #050505;
    }
    .layout-fit .multisplit-content-wrapper #image-holder {
      color: #dac1fd;
    } 
}
