css:
@-moz-document url-prefix("http://www.toro-league.com/forum") {
/* Super Mario Style Red
-------------------------------------------------*/

/*  	
--------------------------------------------------------------
Colours and backgrounds for common.css
-------------------------------------------------------------- */

html, body {
	color: #24395d;
	background: #2B2E36 url("http://i.imgur.com/yNmWt6c.png") fixed repeat;
}

hr {
    border-color: #09151F;
}

h3 {
    border-bottom-color: #09151F;
}

/* Round cornered boxes and backgrounds
---------------------------------------- */

.headerbar {
	background: #9E373E url("http://i.imgur.com/DPiu2vh.jpg") no-repeat right bottom;
	color: #FFFFFF;
}

.navbar {
	background: url("http://i.imgur.com/H20GxtQ.png") repeat-x #9E373E;
}

.forabg {
	background-color: #56b2df;
	background-image: url("http://i.imgur.com/s5Blkje.png");
}

.forumbg {
	background-color: #56b2df;
	background-image: url("http://i.imgur.com/36FXB70.png");
}

.panel {
	background-color: #e0fdff;
}

.bg1	{ background-color: #FFFFFF; 
    border: 1px dotted #4895bb; }
.bg2	{ background-color: #FFFFFF; 
    border: 1px dotted #4895bb; }
.bg3	{ background-color: #FFFFFF;
    border: 1px solid #4895bb; }

/*  	
--------------------------------------------------------------
Colours and backgrounds for content.css
-------------------------------------------------------------- */

ul.forums {
    background-color: #e0fdff;
}

ul.topiclist dd {
	border-left-color: #e0fdff;
}

li.row:hover {
	background-color: #b7dff8;
}

li.row:hover dd {
	border-left-color: #b7dff8;
}

/* Super Mario Style

*_*  Top Menu  *_*
-------------------------------------------------*/

.ddcolortabs a {
	color: #FFFFFF;
	background-color: #4895bb;
}

.ddcolortabs a:link {
	color: #FFFFFF;
}

.ddcolortabs a:hover {
	color: #FFFFFF;
	background-color: #4895bb;
}

.ddcolortabs a:hover span {
	color: #FFFFFF;
	background-color: #56b2df;
}

.ddcolortabs a:visited {
	color: #FFFFFF;
}

/*  	
--------------------------------------------------------------
Colours and backgrounds for links.css
-------------------------------------------------------------- */

a:link	{ color: #09151F; }
a:visited	{ color: #09151F; }
a:hover	{ color: #102536; }
a:active	{ color: #102536; }

/* Links for forum/topic lists */
a.forumtitle {
	color: #102536;
}

/* a.forumtitle:visited { color: #105289; } */

a.forumtitle:hover {
	color: #00407F;
}

a.forumtitle:active {
	color: #00407F;
}

a.topictitle {
	color: #102536;
}

/* a.topictitle:visited { color: #368AD2; } */

a.topictitle:hover {
	color: #00407F;
}

a.topictitle:active {
	color: #102536;
}

/* Post body links */
.postlink {
	color: #09151F;
}

.postlink:visited {

}

.postlink:active {
	color: #09151F;
}

.postlink:hover {
}

/* Content container styles
----------------------------------------*/
.content h2, .panel h2 {
    border-bottom-color: #102536;
}

/* Post author */
.signature {
    border-top-color: #4895bb;
}

/* Miscellaneous styles
---------------------------------------- */
p.rules {
	background-color: #e0fdff;
}
div.rules {
	background-color: #e0fdff;
}

/* CP tabbed menu
----------------------------------------*/
#tabs a {
	background-image: url("http://i.imgur.com/m6r1BT3.png");
}

#tabs a span {
	background-image: url("http://i.imgur.com/tOi7iSN.png");
}

#tabs .activetab a {
	border-bottom-color: #4895bb;
}

/* UCP navigation menu
----------------------------------------*/

/* Link styles for the sub-section links */
#navigation a {
	background-color: #b7dff8;
}

#navigation a:hover {
	background-color: #4895bb;
	color: #FFFFFF;
}

#navigation #active-subsection a {
	color: #4895bb;
}

#navigation #active-subsection a:hover {
	color: #b7dff8;
}

/* Main CP box
----------------------------------------*/

#cp-main h3, #cp-main hr, #cp-menu hr {
	border-color: #b7dff8;
}

/* Table styles
----------------------------------------*/
table.table1 tbody tr:hover, table.table1 tbody tr.hover {
	background-color: #EDCED0;
}

/* Extra Holiday Fun
----------------------------------------*/
div#wrap {
	background-color: #b7dff8;
}

div#inside {
	background-color: #e0fdff;
    border: 1px solid #4895bb;
}

div#tabs {
	background-color: #4895bb;
}



}
