css:
@-moz-document domain("www.lemonde.fr") {
body,
#surheader {
    background: #3a3a38 !important;
    color: #fff !important;
    border-color: #37382c !important;
}
.global {
  background: #3a3a38 !important;
}
a:link,
a:visited,
.link {
    color: #929377 !important;
}
a:active,
.link:active {
    color: #929377 !important;
}
div,
td,
.widget-data-recherche .titre {
    border: none !important;
    background-color: #3a3a38 !important;
    color: #929377 !important;
    font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
    border-color: #37382c !important;
}
.txt1,
.txt2,
.txt3,
.txt3_120,
.txt4,
.txt4_120,
.txt5,
.txt6 {
    font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
}
.col_droite .bloc_base .entete_important,
.col_droite .bloc_appel .entete .titre_appel,
.col_droite .bloc_appel .entete {
    background-color: #37382c !important;
    color: #929377 !important;
}
.btn {
    background-color: #37382c !important;
    color: #929377 !important;
    border-color: #929377;
}
input,
#nav.accueil,
#header_utilisateur .recherche p {
    background-color: #37382c !important;
    color: #717259 !important;
    border: none !important;
}
img {
    filter: contrast(80%);
    filter: saturate(50%);
}
figure img,
article img,
.img_bord,
.widget-data-recherche,
#nav a,
#nav span,
.bordt6 {
    border: none !important;
}
#barre_titre .conteneur_maj .menu_lma_lmfr,
#en_ce_moment li:first-child,
#bt_recrut_abo,
#nav .abonnes,
*+html #surheader>a,
#surheader .bt_abo,
#test_ab_plus_partages,
#appel_jelec,
.partage,
.conteneur_barre_outils {
    display: none;
}
#en_ce_moment {
    background: #32332c !important;
}
input,
#nav.accueil,
#header_utilisateur .recherche p,
#nav_ariane {
    background: #3a3a38 !important;
}
.deplier,
.voir_plus.hovered {
    background: #3a3a38 !important;
    border: none;
}
li {
    border: none !important;
}
.marqueur_decodeurs:before,
.ico_live_mini,
body:not(.app_abonnes) .marqueur_restreint::before,
.bloc_appel {
    display: none
}
}

@-moz-document regexp(".*.lemonde.fr/(.*/article.*|20../.*)") {
section.videos,
div.position_pub,
div[id="bandeau_bas"],
header[id="header"],
nav[id="en_ce_moment"],
nav[id="nav"],
div[id="barre_titre"],
div.bloc_abonnes,
div.en_continu,
.blov_deroule_bloc div.plus_partages,
section.promo,
div[id="footer_services"],
div.footer_gratuit,
div.btn_lecture_zen {
    display: none !important;
}
#main > div#container {
    width: 100% !important;
}
.article {
    padding: 0px !important;
    width: 100% !important;
}
div.alpha {
    float: none !important;
}
#articleBody,
#main > div#container .entry-content {
    padding-left: 150px;
    padding-right: 100px;
    font-size: 150% !important;
    text-align: justify;
    line-height: 150%;
}
article h1,
#main > div#container .entry-title {
    text-align: center;
    font-size: 350% !important;
}
div.grid_12 {
    width: 770px !important;
}
.contenu_article .lire,
.teaser_evenement {
    background: inherit;
}
}
