css:
/**
 * @description
 *     Color theme for Firefox
 *         dark newtab
 *         dark addons pages
 *     Original by Niklas Gollenstede.
 *     Modified by Charles Boury
 *
 *     The original turns a lot more about:pages dark
 *     check it out : https://github.com/NiklasGollenstede/stylish/blob/master/about-pages-dark.css
 */

@-moz-document
    url(about:blank) {
        body {
            background-color: #262626;
        }
}

@-moz-document
    url(about:newtab) {

        body, #newtab-customize-overlay {
            background-color: #262626;
        }

        #newtab-customize-button:-moz-any(:hover, :active, [active]) {
            background-color: #2D2D2D;
            border: none;
        }
}



@-moz-document
    url(about:addons) {
        .main-content,
        #dialogBox {
            background-color: #1E1E1E;
        }
        
        .addon[active="false"] .icon {
            opacity: 0.2;
        }

        .view-header {
           border-bottom: 1px solid #747364;
        }
        /* buttons styling */
        button {
            background-color: #2D2D2D;
            color : #747364;
            border: 0px solid #393939;
        }
        button:hover {
            background-color: #393939 !important;
            color: #e8e2d8;
        }

        .addon {
            border: none;
        }    
        .addon[active="true"] {
            color: #747364;
        }
        .addon[active="false"] .content-inner-container {
            color: #333333 !important;;
        }

        /* selected line */
        .addon[selected][active="true"] {
            background-color: #262626;
            color: #e8e2d8;
        }
        .addon[selected][active="false"]  {
            background-color: #262626;
            color: #56564e;
        }
        .addon[selected][active="false"] .content-inner-container  {
            color: #56564e !important;
        }
        
        .sorter {
            color: #747364;
        }

       .sorter[checkState="1"] {
            background-color: #2D2D2D;
        }

       .sorter[checkState="2"] {
            background-color: #2D2D2D;
        }
        button.button-link {
            color: #ff9500;
        }
        
        
/* boutons en haut Ã  droite */
        
        #header-utils-btn {
            background-color: #2D2D2D;
            border: none;
        }
        .header-button:hover:not([disabled="true"]), 
        #header-utils-btn:hover:not([disabled="true"]) {
            background-color: #393939 !important;
        }
        #header-search {
            background-color: #2D2D2D;
            border: none;
            color : #747364;
        }
        
/* resultats de recherche */
        .radio-check {
            border: none;
            background-color: #2D2D2D;
            background-image: none;
            box-shadow: none;
        }
        
/* details des plugins de l'extension stylish */
        .detail-view-container {
            color: #747364;
        }
        .detail-row, .detail-row-complex {
            text-shadow: none;
        }
        button.button-link:hover {
            color : #ff9500;
        }
        
/* plugins firefox */
        .addon-view[notification], .addon-view[pending] {
            background-image: linear-gradient(90deg, rgba(255,149,0,0.3), transparent 30%);
        }
        .detail-row, .detail-row-complex, setting {
            text-shadow: none;
        }
        button, colorpicker[type="button"], menulist {
            background-color: #2D2D2D;
            color : #747364;
            border: none;
        }
        button, colorpicker[type="button"], menulist {
            background-color: #2D2D2D;
            color : #747364;
            border: none;
        }
        button:hover:not([disabled="true"]), 
        colorpicker[type="button"]:hover:not([disabled="true"]), 
        menulist:hover:not([disabled="true"]) {
            background-color: #393939;
        }
}
