css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('www.mangas-vostfr.com'), domain('www.mangas-vostfr.pro') {
  
  /* Name : Dark-vostfr
  * Version : 1.1
  * Author : GinShiro
  * Latest Update : 27/03/2017
  */
  

  /* Fond d'écran */
  body{
    background: rgb(10,10,10) !important;
  }
  
  
  /* Acceuil, News */
  .article, .single_post, #comments, #commentsAdd{
    background: rgb(20,20,20) !important;
  }
   .theauthor a, .thecategory a {
    color: rgb(110,0,0) !important;
  }
    .latestPost {
    border-bottom-color: black !important;
  }
    /* Barre supérieure */
  .navigation ul, div#header {
    background: rgb(20,20,20) !important;
  }
  
  
  
  /*  Fond d'écran nouveautés */
  #sidebar .widget{
    background: rgb(20,20,20) !important;
  }
    /* "Dernières Sorties" */
  h3, strong{
    color : rgb(150,150,150) !important;
  }
  
  

  

  
  
  /* menu déroulant alphabétique */
  .su-spoiler.my-custom-spoiler, .su-spoiler-title {
    background: rgb(10,10,10) !important;
  }
    .su-spoiler.my-custom-spoiler .su-spoiler-content {
    background: rgb(30,30,30) !important;
    border: black !important;
  }
  
  

  
  /* Titres */
  .single-title, .latestPost .title a, .readMore a, a {
    color: rgb(150,150,150) !important;
  }

  


  /* Recherche */
  .sbutton{
    background-color:rgb(10,10,10) !important;
    color: rgb(150,150,150)!important;
  }
  
  #s, .mts-subscribe input[type="text"], .widget_product_search .search-field {
    background-color: rgb(50,50,50) !important;
    color: rgb(150,150,150) !important;
    box-shadow:  none !important;
    border: rgb(50,50,50) !important;
  }
  

  
  
  /* Pages */
  .custom-pagg li.current {
    background: #080808;
    cursor: pointer;
}
  .custom-pagg li:hover {
    background: #353535 !important;
    cursor: pointer;
}
  

  
      /* Description vidéo */
    span {
    color: rgb(150,150,150)!important;
  }
    /* Espace commentaire */
  #comments .avatar {
    display: none !important;
  }
  .commentlist > li div  {
    background: rgb(10,10,10) !important;
    color: rgb(150,150,150) !important;
  }
  .reply a{
    color: rgb(200,200,200) !important;
  }
  

  /* Lecteurs */
  .su-tabs.my-custom-tabs .su-tabs-nav span:hover {
    background: #8F8F8F !important;
    border: 1px solid #0b0b0b;
}
  .su-tabs.my-custom-tabs .su-tabs-nav span {
    border: 1px solid #000000;
    background: #444;
    color: #050504;
    font-size: 1.1em;
    text-shadow: 2px 2px 4px #000000;
}
  .su-tabs.my-custom-tabs .su-tabs-nav span.su-tabs-current {
    background-color: #0F0F0F !important;
    color: #f6f3f3;
    border: 1px solid #ffffff;
}
  .su-tabs.my-custom-tabs {
    background-color: #262626;
    max-height: 490px;
    overflow: hidden;
    width: 100%;
} 

  
    /* Partage Facebook et Gmail */
  .shareit.top {
    display: none !important;
  }
  
  
    .copyrights {
    background-color: black !important;
  }
  
  
  
 
}
