css:
@-moz-document domain("telegra.ph") {
.tl_article h1,
.tl_article h2,
.tl_article address,
.tl_article .tl_article_content h3,
.tl_article .tl_article_content h4,
.tl_article .tl_article_content figcaption,
.tl_article .tl_article_content figcaption .editable_input,
.tl_message .tl_tooltip .prompt_input .error_msg .tl_link_tooltip {
    font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", Helvetica, Arial, "PingFang SC", "PingFang TC", "Noto Sans SC", "Noto Sans TC", "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.tl_article .tl_article_content,
.tl_article .tl_article_content .ql-editor,
.tl_article .tl_article_header,
.tl_article .tl_article_content blockquote,
.tl_article .tl_article_content aside {
    font-family: Georgia, Cambria, 'Times New Roman', "PingFang SC", "PingFang TC", "Noto Sans SC", "Noto Sans TC", "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.tl_article .tl_article_content aside {
    text-align: inherit;
}

.tl_article .tl_article_content,
.tl_article .tl_article_content .ql-editor,
.tl_article .tl_article_header {
    line-height: 2;
}

.tl_article address {
    margin-top: 16px;
    margin-bottom: 22px;
}

.tl_article .tl_article_content blockquote,
.tl_article .tl_article_content aside {
    font-style: normal;
}

.tl_article address time::before {
    content: "·";
}
}

