css:
/* LukaWolf */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chatzy.com") {
  BODY {
    background-color: #0a0a0a;
    line-height: 1.21;
    font-family: "Open sans", Arial, Helvetica, sans-serif;
    font-size: 14px;
    color: deepskyblue;
  }
  A {
    color: lightskyblue;
  }
  
  
  A#X1701 {
    color: lightskyblue;
    text-shadow: none;
  }
  .X1676 A, DIV#X2826 A{
    background-color: #1a1a1a;
    color: lightskyblue;
  }
  .X1676 A:hover, #X1191 P, #X1191 P.X5907,
  DIV#X2826 A:hover{
    background-color: lightskyblue;
    color: #1a1a1a;
  }
  #X1191 P:hover, #X1191 P.X5907:hover{
    background-color: #efefef;
  }
  
  DIV#X4060 {
    background-color: #1a1a1a;
  }
  DIV#X4060 A {
    color: lightskyblue;
  }
  
  DIV#X1611 {
    border-right: solid 1px #A6BFA3;
  }
  
  TABLE#X2571 {
    background-color: #1a1a1a;
  }
  #X2325 SPAN.X4177, #X2325 P.b{
    color: lightskyblue;
    background: none;
  }
  #X2325 B.X8556{
    display: inline-block;
    width: 100px;
    height: 25px;
    line-height: 25px;
    padding-left: 10px;
    border-radius: 13px;
    background: rgba(255, 255, 255, 0.1);
    margin-right: 5px;
  }
  #X2325 SPAN{
    color: deepskyblue !important;
  }
  #X2325 DIV.c {
    border-color: rgba(255, 255, 255, 0.1);
  }
  
  FORM#X1521 A#X4221{
    background: rgba(255, 255, 255, 0.1);
  }
  
  FORM#X1521 INPUT {
    background-color: #1a1a1a;
    border: solid 1px #2a2a2a;
    color: deepskyblue;
    padding: 0 10px;
    height: 24px;
  }
  DIV#X1850 {
    background-color: #1a1a1a;
    color: deepskyblue;
  }
}
