css:
/* pinguens's Kuro mod */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(what.cd) {
  span[class="log1"], span[class="log3"],
  span[class*="log5"], span[class*="log4"] { color: #888 !important; text-decoration: none !important; }
  .r10, .r20, .r50, .r99 { color: #62AC21 !important; }
  span[class="stat"] { font-weight: bold !important;
                       font-size: 12px !important; }
  div[id="menu"], input, select, textarea { font-size: 11px !important; }
  div[style=""] { display: none !important; }
  ul[id="userinfo_stats"], ul[id="userinfo_stats"] a { color: #DBDBDB !important; }
  #userinfo_username { right: 5% !important;
                       left: auto !important;
                       margin-right: 10px !important;
                       text-align: right; }
  
  #userinfo_major { margin-top: 15px !important; }
  ul[id="userinfo_minor"] li a[href*="userhistory.php?action=posts"] { display: none !important; }
  ul[id="userinfo_minor"] li a[href*="bookmarks.php"] { display: none !important; }
  ul[id="userinfo_minor"] li a[href*="friends.php"] { margin-left: -6px !important; }
  ul[id="userinfo_minor"] li a[href*="user.php?action=notify"] { margin-left: -6px !important; }
  #menu ul li a:hover { background-color: #616161 !important; }
  #index #nav_index, #torrents #nav_torrents, #requests #nav_requests,
  #forums #nav_forums, #top10 #nav_top10, #rules #nav_rules, #kb #nav_kb, #staff #nav_staff {
	background-color: #616161 !important; }
  a { color: #ddd !important; }
  div[class="box"] { background-color: #232323 !important; color: #ccc !important; }
  div[class="box"] a { color: #abd !important; }
  tr[id*="torrent_"] { color: #ccc !important; }
  blockquote { color: #ccc !important; }
  blockquote a { color: #abd !important; }
  tr[class="group discog"] { background-color: #363636 !important; }
  tr[class="group discog"] a { color: #EEE !important; }
  div[class="tags"] a { color: #BBB !important; }
  div[id="searchbars"] input { background-color: #111 !important; }
  table#torrent_table.torrent_table { width: 950px !important; }
  textarea[name="info"] { width: 640px !important;
                          height: 200px !important; }
  div[id="logo"] { margin-left: 0px !important;
                   margin-bottom: -40px !important; }
  div[id="userinfo"] { margin-bottom: 112px !important;
                       background-color: transparent !important; 
                       width: 600px !important;
                       text-align: center !important;
                       margin-left: auto !important;
                       margin-right: auto !important; }
  div[id="menu"] { margin-top: 0px !important;
                   background-color: #303030 !important; }
  div[id="searchbars"] { margin-top: -60px !important;
                         padding-bottom: 6px !important;
                         background-color: #1c1c1c !important; }
  div[id="content"] { padding-top: 10px !important;
                      margin-top: -45px !important; }
  div[id="reseed_box"] { display: none !important; }
  div[id="recommended"] { display: none !important; }
  div[id="alerts"] { font-size: 12px !important; 
                     margin-top: -43px !important;
                     margin-bottom: 55px !important;
                     padding-bottom: 0px !important; }
  div[class="alertbar"] { 
        border-top: 1px solid #444 !important;
        border-bottom: 1px solid #444 !important;
        padding: 3px 0px 3px 0px !important;
        background-color: #451919 !important;
        text-align: center !important;
        font-weight: bold !important;
        width: 100% !important;
        min-height: 13px !important;
        margin-bottom: 2px !important;
  }

  div[class="alertbar"] a { text-decoration: none !important; }

  div[class="save_message"] {
        border-left: none !important;
        border-right: none !important;
        background-color: #457A19 !important;
        color: #F1F1F1 !important;
  }

  div[class="error_message"] {
        border-left: none !important;
        border-right: none !important;
  }
}
