css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.reddit.com/r/electronic_cigarette/") {

@media screen and (min-width: 1600px) {
  .thing {
    width: 31% !important;
  }
}


@media screen and (min-width: 800px) and (max-width: 1599px) {
  .thing {
    width: 48% !important;
  }
}

@media screen and (max-width: 799px) {
  .thing {
    width: 98% !important;
  }
}

.thing {
  position: relative !important;
  display: block !important;
  float: left !important;
  height: 250px !important;
  margin: 1% 1% !important;
  padding: 0 !important;
  background-color: rgba(240,240,250,0.95) !important;
  -webkit-box-shadow: 0px 26px 20px -20px rgba(0,0,0,0.5) !important;
  box-shadow: 0px 26px 20px -20px rgba(0,0,0,0.5) !important;
  transition-duration: 0.2s !important;
}

.thing.promoted {
  width: 100% !important;
  margin: 0 !important;
  padding: 0 !important;
  height: 180px !important;
}


.thing:hover {
  background-color: rgba(255,255,255,1) !important;
  -webkit-box-shadow: 0px 26px 20px -20px rgba(0,0,0,0.7) !important;
  box-shadow: 0px 35px 30px -15px rgba(0,0,0,0.7) !important;
}

.thing:after {
  content: "." !important;
  display: block !important;
  position: absolute !important;
  width: 100% !important;
  height: 2px !important;
  margin: 0 !important;
  padding: 0 !important;
  background-color: rgba(0,0,0,0.5) !important;
  bottom: 0 !important;
}

#siteTable {
  position: relative !important;
  width: 100% !important;
  float: left !important;
  display: block !important;
  background-color: rgba(70,130,180,0.8) !important;
}

#siteTable:before {
  content: "" !important;
  background-color: #222 !important;
  width: 100% !important;
  height: 20px !important;
  display: block !important;
  text-align: center !important;
  font-size: 1.3em !important;
  color: rgba(200,200,255,0.9) !important;
  line-height: 1.6em !important;
}

#siteTable .clearleft {
  display: none !important;
}

.nav-buttons {
  float: left !important;
  width: 100% !important;
  text-align: right !important;
}
  
}
