css:
@-moz-document url("http://dragonvale.wikia.com/wiki/Special:Chat?useskin=wikia"), url("http://dragonvale.wikia.com/wiki/Special:Chat") {
/* Dark Chat by MrAwesome8 */

.Chat .you {
    background: #333333;
    /* your posts*/
}
#WikiaPage {
    background-color: #272822;
    /* chat background*/
    
    color: #939393;
    /* chat text color */
}
body {
    background: #272822;
    /* outer edge */
    
    color: #cccccc;
}
.Chat {
    border-right: 1px solid #272822;
    border-bottom: 1px solid #272822;
}
a {
    color: #8FA0F8;
    /* link color*/
}
.Chat .inline-alert {
    color: #24577b;
    /* inline alert*/
}
.ChatHeader {
    background-color: #272822;
    /* header background */
    
    border: 1px solid #272822;
    /*header outline */
}
.ChatHeader .User {
    border: 0px solid #272822;
}
.Write [name="message"] {
    background-color: #252526;
    /* text box background */
    
    color: #A2A2A2;
}
.Write .message {
    background-color: #252526;
    /* text box boarder */
    
    border: 1px solid #333333;
    /* text box outline */
}
.wordmark {
    color: #939393;
    /* header text color */
}
.User.away {
    color: #829285;
    /* away color */
}
.UserStatsMenu .info {
    background: #3a3939;
    /* PM menu header */
}
.UserStatsMenu {
    color: #939393;
    /* PM menu header text */
}
.UserStatsMenu {
    background: #4e5052;
    /* PM menu background */
}
.UserStatsMenu .actions ul li,
.UserStatsMenu .actions ul li .label,
.UserStatsMenu .actions ul li a {
    color: #82A0D6;
    /* PM menu text */
}
.UserStatsMenu .actions ul li svg {
    color: #272822;
    /* PM menu icon color */
}
.UserStatsMenu .actions ul li svg:hover,
.UserStatsMenu .actions ul li .label:hover {
    color: #fff;
    /* hover over icons/text color in PM menu */
}
.UserStatsMenu .actions ul.admin-actions li.kick svg:hover,
.UserStatsMenu .actions ul.admin-actions li.ban svg:hover {
    /* .UserStatsMenu .actions ul.admin-actions li.kick .label:hover */
    
    color: #931114;
    /* hover over icons/text color in admin PM menu */
}
.UserStatsMenu .actions ul li.kick:hover,
.UserStatsMenu .actions ul li.ban:hover {
    background-color: #4e090b;
    /* admin PM menu hover color */
}
.UserStatsMenu .actions ul.admin-actions li.give-chat-mod svg:hover {
    color: #eae41a;
    /* give chatmod icon color */
}
.UserStatsMenu .actions ul li.give-chat-mod:hover {
    background-color: #8e7000;
    /* give chatmod hover color */
}
.UserStatsMenu .actions li.private-block svg:hover {
    color: #931114;
    /* private-block icon color */
}
.UserStatsMenu .actions ul li.private-block:hover {
    background-color: #4e090b;
    /* private-block hover color */
}
.UserStatsMenu .actions ul li:hover {
    background-color: #3a3939;
    /* PM menu hover color */
}
#pingspan {
    display: none;
}
.Rail .private {
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(0, #333333), color-stop(100%, #333333));
    /* private message rail */
}
.Rail .selected {
    box-shadow: inset 0 0 7px 0 #323033;
    background: #424242;
    /* main chat selected */
}
.Rail .User:hover {
    background: #3a3939;
    /* hover over user color */
}


.Rail .splotch {
    background-color: #dc0d0e;
    background-image: -moz-linear-gradient(top,#e63031 0,#dc0d0e 50%,#e63031 100%);
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(0,#e63031),color-stop(50%,#dc0d0e),color-stop(100%,#e63031));
    background-image: -o-linear-gradient(top,#e63031 0,#dc0d0e 50%,#e63031 100%);
    background-image: -ms-linear-gradient(top,#e63031 0,#dc0d0e 50%,#e63031 100%);
    border: 1px solid white;
    color: white;
}


#WikiaPage {
    border: 1px solid #272822;
    /* chat room boarder */
}
::selection {
    background: #ffb7b7;
    /* WebKit/Blink Browsers */
}
::-webkit-scrollbar {
    background-color: #333333;
    /* bar */
}
::-webkit-scrollbar-button {
    background-color: #2D2D30;
    /* top and bottom */
}
::-webkit-scrollbar-track {
    background-color: #333333;
    /* bar */
}
::-webkit-scrollbar-track-piece {
    background-color: #333333;
    /* bar */
}
::-webkit-scrollbar-thumb {
    background-color: #252526;
    /* scroller */
}
::-webkit-scrollbar-corner {
    background-color: #333333;
}
::-webkit-resizer {
    background-color: #333333;
}
.User.chat-mod .username:after,
.User.staff .username:after {
    background-image: none !important;
}
.user .username:after {
    right: 6px !important;
}
.User.chat-mod .username:after {
    content: url("http://images2.wikia.nocookie.net/monchbox/images/6/6b/Icon-chatmod.png");
}
}
