css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gasengi.com") {

  body, div, span, table, tbody, tr, td, input, select, textarea,
  .menu_top01 a, .board_numbering a {
    background-color: #171717 !important;
    color: #bcbcbc !important;
    font-family: "나눔바른고딕" !important;
  }

  a:link { 
    color: #bcbcbc !important;
    text-decoration: none !important;
  }
  a:hover { 
    color: #FF00FF !important;
    text-decoration: none !important;
  }
  a:active { 
    color: #FF00FF !important;
    text-decoration: underline !important;
  }
  a:visited { 
    color: #B9A3E3 !important;
    text-decoration: none !important;
  }

  body div, td, td div, 
  .websp_id, .websp_txt, .websp_password {
    background: url('http://') !important;
  }

  #writeContents,
  #writeContents * {
    background-color: #171717 !important;
    color: #bcbcbc !important;
    font-family: "나눔바른고딕" !important;
    line-height: 1.6em !important;
    font-size: 20px !important;
  }

  #commentContents * {
    font-size: 18px !important;
  }

  .Mwebsp_latest,
  #content_right ul,
  .rank_dbox *,
  .subject,
  .websp_subject a,
  #gm1, #gm2, #gm3, #gm4, #gm5, #gm6, #gm7 {
    font-size: 14px !important;
    line-height: 1.6em !important;
  }

  .subject {
    font-size: 18px !important;
    line-height: 1.6em !important;
  }

  #mimenu {
    background: url(http://gasengi.com/images2/Menu_Bg.jpg) no-repeat !important;
  }

  .board_list .notice { color: #99CCFF !important; }
  .current { color: #E15916 !important; }
  .member { color: #00FF00 !important; }
  .commentCount, .comment {
    color: #fa8072 !important;
    font-size: 10px !important;
  }

  #gotop, #logo, #copyright,
  #warring_tops {
    visibility: hidden !important;
    height: 0px !important;
  }

  #mimenu div {
    visibility: hidden !important;
  }

}
