css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zipbogo.net"), domain("mybogo.net") {

body, input, select, textarea,
#footer * {
  font-size: 12px !important;
}

#nav, #nav span,
.top_link, .sub, .sub a {
  font-size: 13px !important;
  font-weight: bold !important;
}

body, input, select, textarea,
#nav, #nav span, #page, #page *, #footer,
.top_link, .sub, .sub a {
  font-family: "맑은 고딕", "나눔고딕" !important;
}

body, td, input, select, textarea, 
#nav, #nav span, #page, #page p, #footer, #link_list, 
.top_link, .sub, .sub a, .board01 *, .entry * {
  background-color: #171717 !important;
  color: #bcbcbc !important;
}

a:link { 
  color: #bcbcbc !important;
  text-decoration: none !important;
}
a:hover { 
  color: #FF00FF !important;
  text-decoration: none !important;
}
a:active { 
  color: #FF00FF !important;
  text-decoration: underline !important;
}
a:visited { 
  color: #B9A3E3 !important;
  text-decoration: none !important;
}

#logo * {
  visibility: hidden !important;
}

#logo_img {
  margin: 0px 0 !important;
}

#logo, #header, #logo_img, #logo_img a img {
  height: 0px !important;
}

#menu {
  float: left !important;
  background: #171717 !important;
  
}

#menu_under {
  background: url('http://') repeat-x left top !important;
  background-color: #171717 !important;
}

#page * {
  background-color: #171717 !important;
}

#content div table tbody tr td font b {
  color: #bcbcbc !important;
  font-weight: bold !important;
}

.sidebar li h2 {
  margin: 0 0 0 0 !important;
  padding: 5px 15px !important;
  background: #d7e7e7 !important;
  font-size: 13px !important;
  color: #bcbcbc !important;
  border: 0px #c9c9c9 solid !important;
}

A.Font3 {
  text-decoration: none !important;
  color: #ffffff !important;
  font-size: 11px;
}

.post {
  line-height: 160% !important;
}

.num tr td span {
  color: #99CCFF !important;
}
  
}
