css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("todayhumor.co.kr") {

body, table, tbody, tr, td, h1, h2, h3, h4, input, button, textarea, select, div {
  font-size: 12px !important;
  font-family: "맑은 고딕", "나눔고딕" !important;
}

body, input, button, textarea, select,
#topmenu_line0, #topmenu_line1, #topmenu_line2, #topmenu_line3, #menu_explain_box,
.whole_box, .vertical_container, .table_list, .table_container, .list_ad, .writerInfoContents, .rereMemoDiv, .viewContent {
  background-color: #171717 !important;
  color: #bcbcbc !important;
}

.viewContent *, #board_map table tbody tr td {
  font-family: "맑은 고딕", "나눔고딕" !important;
  background-color: #171717 !important;
  color: #bcbcbc !important;
}

a:link { 
  color: #bcbcbc !important;
  text-decoration: none !important;
}
a:hover { 
  color: #FF00FF !important;
  text-decoration: none !important;
}
a:active { 
  color: #FF00FF !important;
  text-decoration: underline !important;
}
a:visited { 
  color: #B9A3E3 !important;
  text-decoration: none !important;
}

.writerInfoBgLeft, .writerInfoBgMiddle, .writerInfoBgRight, .viewSubjectDiv {
  background: url('http://') !important;
}

.writerInfoContents div a font b {
  color: #00FF00 !important;
}

.memoDivBlue1 {
  background-color: #5A5A5A !important;
}

.memoDivBlue2 {
  background-color: #3C3C3C !important;
}

.memoWriteMemo {
  background-color: #025702 !important;
}

.list_memo_count_span {
  color: #fa8072 !important;
  font-size: 10px !important;
}

.name a {
  color: #00FF00 !important;
}

.hits {
  color: #A0C5E8 !important;
}

.oknok {
  color: #FFEEBC !important;
}

.list_ad, .ad_adsense {
  visibility: hidden !important;
}

}
