css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvcast.naver.com"), domain("tv.naver.com") {
  
  body, input, button, textarea, select {
    font-family: "맑은 고딕", "나눔고딕" !important;
  }

  body, input, button, textarea, select {
    font-size: 12px !important;
  }

  body, input, button, textarea, select,
  #footer, #menuScrollBox, #container, #topChannelInfo, #content *, 
  .player_container, .player {
    background-color: #171717 !important;
  }

  body, input, textarea, select, 
  h1, h2, h3, h4, h5, h6, div, span, p,
  .tit, .col {
    color: #bcbcbc !important;
  }

  a:link { 
    color: #bcbcbc !important;
    text-decoration: none !important;
  }
  a:hover { 
    color: #FF00FF !important;
    text-decoration: none !important;
  }
  a:active { 
    color: #FF00FF !important;
    text-decoration: underline !important;
  }
  a:visited { 
    color: #B9A3E3 !important;
    text-decoration: none !important;
  }

  #header .header_bg {
    background-color: #171717;
    /*background-image: -webkit-gradient(linear,left top,right top,color-stop(0,#696969),color-stop(0.5,#4B4B4B),color-stop(1,#2D2D2D));*/
    background-image: linear-gradient(to right,#696969 0,#4B4B4B 50%,#2D2D2D 100%);
    transition: opacity 500ms linear;
  }

  /*.player { background-color: #3C3C3C !important; }*/

}
