css:
@-moz-document url-prefix("http://blocgame.com") {
#ad_unit, #adContent-clickOverlay, .img_ad, .adsbygoogle, #content > div:first-child {
	visibility: hidden !important;
	display: none !important;
	height: 0 !important;
	width: 0 !important;
}

body {
	background: none;
	background-color: #263238;
}

#container {
	width: 90%;
}

#content {
	padding: 20px;
	background: none;
	background-color: rgba(0,0,0,0);
}

#con_div {
	background: none;
	margin-top: 0;
}

#content > .accordion > div:last-child {
	background-color: #212121 !important;
	border-radius: 8px;
}

.table > tbody > tr:nth-child(odd) > td {
	background-color: #37474F;
}

.table > tbody > tr:nth-child(even) > td {
	background-color: #212121;
	border-radius: 0 0 8px 8px;
}

.table > tbody, .table-striped > tbody > tr { 
	border-radius: 8px;
}


.table > tbody > tr:first-child > td:first-child {
	border-radius: 8px 0 0 0;
}

.table > tbody > tr:first-child > td:last-child {
	border-radius: 0 8px 0 0;
}

.table, .table-stripped > tbody {
	border-radius: 8px;
}

.table > tbody > tr > td {
	border: none;
}
}

@-moz-document url("http://blocgame.com/economics.php"), url("http://blocgame.com/domestic.php"), url("http://blocgame.com/foreign.php"), url("http://blocgame.com/military.php"), url("http://blocgame.com/market.php") {
#content > table > tbody > tr > td > a > h2 {
	margin-top: 10px;
}

#content > table > tbody > tr > td {
	transition: all 300ms ease-in-out;
	opacity: .8;
}

#content > table > tbody > tr > td:hover {
	opacity: 1;
}
}

@-moz-document url("http://blocgame.com/market.php") {
#content > center > center > table > tbody > tr > td:last-child > button {
	transition: all 200ms ease-in-out;
}

#content > center > center > table > tbody > tr > td:last-child > button:hover {
	do: stuff
}
}
