@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kobold.club") {
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.42857;
    color: #111;
    background-color: #5d5d5d;
    }
    .bg-info {
    background-color: #4a4a4a;
    }
    h2 {
        color: #777;
    }
    .text-muted {
    color: #222;
}
    
     /*
    * — Difficulty Colors —
    */ 
    .table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #777;
    }
    .table-striped > tbody > tr:nth-of-type(2n) {
    background-color: #666;
    }
    
    /*
    * — Monster Table & Colors —
    */ 
    .table-bordered {
    border: 1px solid #111;
   }
   .table-bordered > thead > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > th, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > th, .table-bordered > tfoot > tr > td {
    border: 1px solid #111;
   }
       .monster-table--cr-cell__trivial {
    background-color: #edd7d7;
              color: #111;
              font-weight: bold;
    }
       .difficulty-legend__trivial {
    background-color: #edd7d7;
    }
    
       .monster-table--cr-cell__group {
    background-color: #d39d9d;
             color: #111;
             font-weight: bold;
    }    
       .difficulty-legend__group {
    background-color: #d39d9d;
    }
    
        .monster-table--cr-cell__pair {
    background-color: #b86262;
             color: #111;
             font-weight: bold;
    }
       .difficulty-legend__pair {
    background-color: #b86262;
    }
    
       .monster-table--cr-cell__easy {
    background-color: #963535;
            color: #ddd;
            font-weight: bold;
    }
       .difficulty-legend__easy {
    background-color: #963535;
          color: #ddd;
    }
    
       .monster-table--cr-cell__medium {
    background-color: #742929;
            color: #ddd;
            font-weight: bold;
    }
       .difficulty-legend__medium {
    background-color: #742929;
          color: #ddd;
    }
    
       .monster-table--cr-cell__hard {
    background-color: #531d1d;
            color: #ddd;
            font-weight: bold;
    }
       .difficulty-legend__hard {
    background-color: #531d1d;
          color: #ddd;
    }
    
       .monster-table--cr-cell__deadly {
    background-color: #321111;
            color: #ddd;
            font-weight: bold;
    }
       .difficulty-legend__deadly {
    background-color: #321111;
          color: #ddd;
    }
    
    /*
    * — Forms —
    */
    .form-control{
        color: #333;
    background-color: #777;
    border-color: #ccc;
    border: 1px solid #222;
    }

    /*
    * — Buttons —
    */
    .btn-danger {
    color: #ddd;
    background-color: #800000;
    border-color: #1a0000;
    }
    .btn-default {
    color: #333;
    background-color: #777;
    border-color: #ccc;
    border: 1px solid #222;
    }
    .btn-success:focus, .btn-success.focus {
    color: #fff;
    background-color: #449d44;
    border-color: #255625;
    }
    .btn-success {
    color: #fff;
    background-color: #5cb85c;
    border-color: #4cae4c;
    }

     /*
    * — Nav buttons —
    */
    .pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.42857;
    text-decoration: none;
    color: #fff;
    background-color: #222;
    border: 1px solid #666;
    margin-left: -1px;
    }
    .pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    color: #fff;
    background-color: #222;
    border-color: #666;
    cursor: not-allowed;
    }
    .pagination > .active > a, .pagination > .active > a:hover, .pagination > .active > a:focus, .pagination > .active > span, .pagination > .active > span:hover, .pagination > .active > span:focus {
    z-index: 3;
    color: #fff;
    background-color: #004080;
    border-color: #999;
    cursor: default;
    }
}
