css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Cities: Skylines Mods Catalogue */

@-moz-document domain("smods.ru") 
{

*:not([class^="fa fa"]) {
  font-family:  Arial, Tahoma     !important;  
}  
* {
  -moz-transition:  none  !important;  
  transition:  none  !important;  
}  
  
  
#header    {
  background-color:  #214F79  !important;
  background-image:  url("http://smods.ru/wp-content/uploads/2017/03/zcIDh43.jpg") !important;
  background-repeat: no-repeat  !important;  
  background-attachment: fixed   !important;
}  
#header  ,  
#header  *  {
  opacity: 1  !important;
}  
#header [class="container group"] ,
#header [class="container group"] [class="group pad"] ,
#nav-header {
  background-color:  transparent !important;
}  
#header {
  position: fixed !important; 
  z-index: 55 !important; 
  top: 27px !important;
  max-height: 70px !important;
  height: 70px !important;
  width: 100%  !important;
  min-width: 90%  !important;
  padding-top: 0px !important;
  margin-top: 0px !important;
  padding-bottom: 0px !important;
  margin-bottom: 0px !important;
}
  
#header a {
  color: #fff !important;
}  
#header a:hover {
  color: #FFFF00 !important;
}    
  
/* 1 полоса */ 
/* страницу не листали */   
#nav-topbar:not([style*="transform:"]) {
  position:   fixed   !important; 
  margin-top: 0px !important;	 
  margin-bottom: 0px;
  top: 0px !important;
  z-index: 66 !important; 
}  
/* страницу листали */   
#nav-topbar[style*="transform:"] {
  position:   fixed   !important; 
  margin-top: 0px !important;	  
  margin-bottom: 0px;
  top: 0px !important;
  z-index: 66 !important; 
  -moz-transform:    none !important; 
}  
#nav-topbar [class="nav-wrap container"] > ul > li .sub-menu {
}    
#nav-topbar ,
/* вып. поиск */  
.search-expand-inner {
  background:   #0B2951   !important;   /*    0A1029 */
}
/* 2+3 полоса */  
#header [class="container group"] p {
  color: #CCFFFF !important;
}   
#header [class="container group"]  {
} 
/* 2 полоса (SKYMODS) */    
#header [class="container group"] [class^="group pad"] {
  padding-top: 0px !important;
  padding-bottom: 0px !important;
  margin-top: 0px !important;	
  margin-bottom: 0px !important;
  max-height: 70px !important;
  height: 50px !important;
/* если надоел       */   
  display: none !important;
}  
/* ссылка SKYMODS */
.site-title a {
  position: fixed !important; 
  top: -4px !important;      /* -6px   42px */
  z-index: 85 !important; 
  padding-left: 0px !important;
  margin-left: calc( 770px + 15vw  )  !important;    /*    calc( 740px + 10vw  ) */
  font-weight: normal !important;      /*   normal bold */  
  font-size: 23px !important; 
  /* если надоел       */ 
  display: none !important;
}  
/* Cities: Skylines Mods Catalogue */
#header [class="container group"]  .site-description ,
#header [class="container group"] [class="group pad"] .description-index {
  display: none !important;
}  
/* 3 полоса -  News    Guides    Discussions    Buy    How to Install mods    About Us  */ 
#nav-header {
  position: fixed !important; 
  margin-top: 0px !important;	 
  margin-bottom: 0px;
  top: 47px !important;   /*   47px */           
  z-index: 55 !important; 
  margin-left:  calc( 0.02vw - 11px )  ;    /*     calc( 1vw - 22px )  */
}  
  
  
  
  
  
/*  page  *********************** */

#page  {
  margin-top: 97px !important;
}  
  
#page a {
  color: #2448B6 ;
}  
#page a:hover {
  color: #CC0000 ;
}  
/* кнопки-полоски */  
#page a.wbutton ,
/* на 
http://transport-fever.smods.ru/
http://stellaris.smods.ru/
http://hearts-of-iron-4.smods.ru/   */
[class*="entry"][class*="excerpt"] > p > a.post-excerpt-download ,    
[class*="entry"][class*="excerpt"] > p > a.post-excerpt-more ,
SECTION.content [class="pad group"] a.post-excerpt-download
{
