}  
#page [class="post-date"] {
  color:  #7F4935 !important;  /*    ор. E65C00         */
  font-weight: bold ;   
}    
/*   звездочки  */
#page [class="post-meta group"]  .post-rating {
  transform:  scale(0.85)  ; 
  position: relative   !important;
  top: 2px !important;
}
  

  
.post-list.group .post-row {
