}
    
/* Рекомендуем к просмотру */    
DIV[class="darkrow1"][style="height:5px"] + TABLE[width="100%"][cellspacing="1"][cellpadding="5"] * {
    display :none !important;
} 
    
#footer-counters, 
#header-container > div:nth-child(3) > div:nth-child(6) {
    display :none !important;
} 
    
#right-column > *:not([id="scrollTop"])  {
    display: none !important;
}
/* линк "Наверх" */
#scrollTop {
    margin-right: -175px !important;
}
    
.lenta {
    padding-left: 60px  !important;  
    padding-right: 60px !important;
}  


    
/* синяя полоска */
#main-menu {
    background: rgb(70, 121, 203) !important;
}
#main-menu a:not([id="new-post"]):hover {
    background-color:  #CC0000 !important;
    color: inherit !important; 
    text-decoration:  none !important;  
}
/* Инкубатор */   
#main-menu a[href*="inkubator.yaplakal"]:hover  {
    color: #FFFF00 !important; 
}   
/* рекламный линк */   
#main-menu a > span {
    color: #33FFFF !important; 
}      
#main-menu a:hover > span {
    color: inherit !important; 
}    
/* Новый пост */    
#main-menu a[id="new-post"]:hover  {
    color: inherit !important; 
    filter: saturate(180%) hue-rotate(-80deg)  ;
    text-decoration:  none !important;  
}
/* подменю */    
ul[class="drop-menu no-wrap"] > li > a {
    font-weight: bold !important;  
}    
ul[class="drop-menu no-wrap"] > li > a:hover {
    background-color:  #CC0000 !important;
    color: #FFFFFF !important;  
    text-decoration:  none !important;      
}
  
/* рега и вход */    
#welcome-box {
    background-color: transparent !important; 
    border: 2px solid rgb(90, 141, 203) !important; 
    border-radius: 7px   !important;
    color: #000 !important; 
    width:  308px   !important;
    height: 58px   !important;
    margin-top: 5px !important;
    line-height:  1.7  !important;  
    vertical-align:   middle !important;
} 
#welcome-box > a  {
    color: rgb(90, 141, 203) ; 
}

#login-box {
    background-color: rgb(70, 121, 203)  !important; 
}  
#login-box > form {
    margin-top: 3px !important;   
}
#login-box a:hover {
    color: #FFFF00 !important;     
}  

.postcolor a ,
#QUOTE a  {
    color: #264EC5 ; 
}
a:hover {
    color: #c00 !important;
}
  
/* заголовок поста */
TABLE[id^="p_row_"] td[class="row4"] , 
div.post-rank  {
    background-color:  #F3E4D3   !important;  /* CCEFFF  ECF2FC */
}
/* промежуток */
div.darkrow1  {
    background-color: transparent !important;   /*  #fff */
}

/*  уменьшаем большие скрины */ 
TABLE[id^="p_row_"] tr.collapsebox > TD[class^="post"]:last-child img:not([src="1px.gif"]) {
    height: auto !important;
    max-width: 699px !important;
}      
    
    
#tabs a {
    color: #264EC5 ; 
}        
#tabs a:hover {
    background-color: transparent ; 
}        
     
    
    
    
