css:
@-moz-document domain("typescriptlang.org") {
body,
.examples {
  background-color: #333;
}
h1,
p,
.community .banner-text,
.community .callouts,
.home h1,
.home p,
.home .middle-row p {
  color: #eee;
}
/* Menu */

.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus {
  color: #eee;
  background: #333;
}
.docs-container .docs-nav,
.docs-container .docs-nav .nav li a,
.docs-container .docs-nav .nav .panel.collapsed a.collapsed {
  color: #eee;
}
.docs-container .docs-nav .nav .panel.collapsed a.collapsed:hover,
.docs-container .docs-nav .nav li a:hover,
.docs-container .docs-nav .nav li a:focus,
.docs-container .docs-nav .nav .panel.collapsed a:not(.collapsed) {
  color: #7ac1ff !important;
}
/* Docs */

.docs-container {
  background: #444;
}
.docs-container .post-content,
.docs-container p,
.docs-container .post-header .post-title {
  color: #ccc;
}
.docs-container pre {
  color: #232425;
  background: #333;
}
.docs-container code.language-tree {
  color: #fff;
  background: transparent;
}
.docs-container .post-content h1 {
  color: #eee;
}
.docs-container .post-content h2,
.docs-container .post-content h3,
.docs-container .post-content h4,
.docs-container .post-content h5,
.docs-container .post-content h6 {
  color: #398cff;
}
.docs-container pre code {
  color: #fff;
  background: #333;
}
.docs-container td,
.docs-container th {
  color: #eee;
  background: #333;
}
/* non-docs */

.home .skyline-container {
  background: #333;
}
.home .swimlane_friends_of_typescript .friend-item,
.exampleBox .front-face,
.exampleBox .back-face {
  color: #eee;
  background-color: #444;
}
.next-steps-container {
  color: #eee;
  background-color: #444;
}
.next-steps-container .row > div .next-step-header {
  color: #eee;
}
/* hljs code viewer */

.hljs-string,
.hljs-parent,
.hljs-built_in,
.hljs-filename,
.hljs-value,
.hljs-blockquote,
.hljs-addition,
.hljs-tag,
.hljs-argument,
.hljs-link_label,
.hljs-header,
.hljs-name {
  color: #fff;
}
.hljs {
  display: block;
  overflow-x: auto;
  padding: 0.5em;
  background: #444;
}
.hljs-keyword,
.hljs-selector-tag,
.hljs-literal,
.hljs-section,
.hljs-link {
  color: white;
}
.hljs,
.hljs-subst {
  color: #ddd;
}
.hljs-type,
.hljs-attribute,
.hljs-params,
.hljs-symbol,
.hljs-bullet,
.hljs-built_in,
.hljs-addition,
.hljs-variable,
.hljs-template-tag,
.hljs-template-variable {
  color: #98cfff;
}
.hljs-string,
.hljs-title,
.hljs-name,
.hljs-quote,
.hljs-deletion,
.hljs-meta {
  color: #ffac83;
}
.hljs-keyword,
.hljs-literal {
  color: #60a6ff;
}
.hljs-title {
  color: #28dca3;
}
.hljs-comment {
  color: #388234;
}
.hljs-keyword,
.hljs-selector-tag,
.hljs-literal,
.hljs-title,
.hljs-section,
.hljs-doctag,
.hljs-type,
.hljs-name,
.hljs-strong {
  font-weight: bold;
}
.hljs-emphasis {
  font-style: italic;
}
/* monaco editor */

.monaco-editor,
.monaco-editor .inputarea,
.monaco-editor-background {
  background: #333!important;
}
.monaco-editor.vs .token.terminal {
  color: #BD5050;
}
.monaco-editor.vs .token.terminal.code1 {
  font-weight: bold;
}
.monaco-editor.vs .token.variable {
  color: #74B0DF;
}
.monaco-editor.vs .token.variable.predefined {
  color: #4864AA;
}
.monaco-editor.vs .token.constant {
  color: #dd0000;
}
.monaco-editor.vs .token.string {
  color: #CE9178;
}
.monaco-editor.vs .token.string.escape {
  color: #CE9178;
}
.monaco-editor.vs .token.comment {
  color: #929292;
}
.monaco-editor.vs .token.identifier {
  color: #28dca3
}
.monaco-editor.vs .token.comment.shebang {
  color: #929292;
}
.monaco-editor.vs .token.comment.doc {
  color: #608B4E;
}
.monaco-editor.vs .token.literal {
  color: #e00000;
}
.monaco-editor.vs .token.literal.hex {
  color: #e07000;
}
.monaco-editor.vs .token.number {
  color: #B5CEA8;
}
.monaco-editor.vs .token.number.hex {
  color: #5BB498;
}
.monaco-editor.vs .token.number.binary {
  color: #3030c0;
}
.monaco-editor.vs .token.number.octal {
  color: #204070;
}
.monaco-editor.vs .token.regexp {
  color: #B46695;
}
.monaco-editor.vs .token.delimiter {
  color: #DCDCDC;
}
.monaco-editor.vs .token.tag {
  color: #569CD6;
}
.monaco-editor.vs .token.metatag {
  color: #DD6A6F;
}
.monaco-editor.vs .token.annotation {
  color: #cc6666;
}
.monaco-editor.vs .token.key {
  color: #9CDCFE;
}
.monaco-editor.vs .token.attribute.name {
  color: #9CDCFE;
}
.monaco-editor.vs .token.attribute.value {
  color: #CE9178;
}
.monaco-editor.vs .token.info-token {
  color: #6796e6;
}
.monaco-editor.vs .token.warn-token {
  color: #cd9731;
}
.monaco-editor.vs .token.error-token {
  color: #f44747;
}
.monaco-editor.vs .token.debug-token {
  color: #b267e6;
}
.monaco-editor.vs .token.keyword {
  color: #569CD6;
}
.monaco-editor.vs .token.keyword.flow {
  color: #C586C0;
}
.monaco-editor.vs .token.keyword.datatype {
  color: #569CD6;
}
}
