css:
@-moz-document url("https://evrl.to/offtop/"), url("https://evrl.to/offtop/?tab=pantheon"), url("https://evrl.to/offtop/?tab=rating"), url("https://evrl.to/offtop/?tab=altar") {
.control-container {
    font-size: 11px;
    padding: 16px;
    background: #3a4950;
    /* color: red; */
}

body {
    margin: 0;
    font-family: Tahoma, Arial;
    font-size: 12px;
    color: #000;
    background: #1F282D !important;
    padding-top: 50px;
    line-height: 1.2;
    height: 100%;
}

.talk-add-poll, .talk-del-poll {
    float: right;
    font-size: 11px;
    margin-top: -19px;
    display: none;
    margin-right: 7px;
}
a {
    color: #000000;
}

a {
    color: #337ab7;
    text-decoration: none;
}

.talks_message_body {
    line-height: 160%;
    color: #fff;
    font-size: 11px;
    margin: 0px 0 0 1px;
    word-wrap: break-word;
}

.feed-item-content {
    background: #263238;
    padding: 16px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    border: none;
}

.game-result .header {
    position: relative;
    margin: -16px 0 0 -16px;
    padding: 16px 0 16px 16px !important;
    border-bottom: 1px solid #eee;
    background: #3a4950;
    border-radius: 2px 2px 0 0;
}

.topbutton .file-topbtn:hover, .topbutton .file-topbtn:focus, .topbutton .file-topbtn.checked:hover, .topbutton .file-topbtn.checked {
    background: transparent !important;
    color: #ffffff !important;
    border-bottom: 2px solid #42A5F5;
}

.gblock {
    background: #3a4950;
    padding: 16px;
    margin: 16px 0 16px 0;
    border-radius: 2px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    position: relative;
}

body {
    margin: 0;
    font-family: Tahoma, Arial;
    font-size: 12px;
    color: #fff;
    background: #1F282D !important;
    padding-top: 50px;
    line-height: 1.2;
    height: 100%;
}

.get_more {
    text-align: center;
    padding: 8px 0;
    margin: 10px;
    background: #3c4f58;
    font-size: 11px;
    color: #ffffff;
    cursor: pointer;
}

.feed-item-comments {
    background: #263238;
    padding: 8px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    border: none;
    margin: 1px 8px 0 8px;
}

.game-result .header .right_subscribe {
    padding-right: 16px;
    font-size: 11px;
    color: #ffffff;
    float: right;
}

.file-topbtn {
    display: inline-block;
    padding: 10px 20px;
    text-align: center;
    background: #E9EDF1;
    width: 219px;
    font-size: 11px;
    color: #3999ff;
    -webkit-transition: background-color 250ms linear;
    transition: background-color 250ms linear;
}

element.style {
    padding: 1px;
    border: 1px solid #1f282d;
}

.talk-reply {
    padding: 10px 10px 8px 10px;
    border-bottom: 1px solid #1f282d;
    position: relative;
}

.talkbox.gametalkbox {
    background: #263238;
    border-top: 1px solid #1f282d;
    border-radius: 0 0 2px 2px;
    margin: -8px -8px -8px -8px;
    padding: 8px;
}

element.style {
    background: rgb(38, 50, 56);
}

.talks_message_bottom {
    line-height: 18px;
    font-size: 11px;
    color: #ececec;
    margin: 0px 0 0 1px;
    word-wrap: break-word;
}

.premium-rank {
    color: #ececec;
    font-weight: normal !important;
    float: none !important;
}

.avatar_square_link img {
    float: left;
    width: 50px;
    height: 50px;
    border-radius: 50% 50% 50% 50%;
}

.mg_article .mg_text {
    padding: 5px 10px;
    font-family: "Roboto", Tahoma;
    color: #ffffff;
    font-size: 15px;
    line-height: 26px;
    font-weight: 400;
}

element.style {
    /* background: rgb(255, 255, 255); */
}

.ps-container {
    overflow: hidden !important;
    background-color: #263238;
}

.talkbox .send_container {
    margin-left: 0;
    position: relative !important;
    overflow-y: auto;
    overflow-x: hidden;
    border-radius: 3px;
    margin-bottom: 0;
    border: none;
}

.feed-item img {
    max-width: 99.9%;
    border: 1px solid #1f282d !important;
    overflow-x: hidden;
    margin-bottom: 0;
    /* border: none; */
    }

.feed-item-comments .feed-item-content {
    padding: 0;
    box-shadow: none;
    border-radius: 0;
    background-color: #263238 !important;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: white;
}

.hide-comments:hover {
    background: #1f282d;
}
}
