css:
/* AGENT_SHEET */

/* 
Начальная страница Mozilla Firefox 
about:home
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home")
{

:root {
  --fon-bl:  rgba(20, 20, 20, 0.65)  ;    /*  var(--fon-bl) 
                                                  transparent 
                                                  rgba(60, 60, 60, 0.30) 
                                                  rgba(128,128,128, 0.15)   */
  --fon-str: rgba(40, 40, 40, 0.7) ;      /*  var(--fon-str)  */
  --str-c:   var(--beg-c)  ;   /*  var(--str-c) 66CCCC     */
    
  --bor-str: rgba(0,0,0, 1) ;  /*  var(--bor-str)  */
  --bor-bl:  var(--bor-str) ;  /*  var(--bor-bl)  rgba(102, 102, 102, 0.60)   */

  --tit:     #dedede ;         /*  var(--tit)      #ddd     */
  --beg-bg:  #333 ;            /*  var(--beg-bg) */
  --beg-c0:  #b3b3b3 ;         /*  var(--beg-c0)   #ccc     */
  --beg-c:   #25E3FD ;         /*  var(--beg-c)  3FFDFD FFFF0F  02CFFD  */
      
  --beg-bor: var(--beg-c) ;         /*  var(--beg-bor) #66CCCC     */

  --light0:  var(--beg-c0) ;   /*  var(--light0)   D2D2D2     */      
  --light:   #15FDFD ;         /*  var(--light)   95FDFD rgba(100,240,255, 0.99)  #64F0FF  */      
}    

/*     сердечки убрать */
[class^="heart-background"]  {
    display: none !important;
}  
    
    
html{
   background: rgb(24,24,24) !important; 
}

html body {
  font-family: sans-serif;
  color: rgb(166,166,166);
  height: 100%;

/* background-image: linear-gradient(35deg, 
      #202020, rgba(43, 43, 60, 1.0) ,  #000000   ) !important;  
background-image: -moz-linear-gradient(45deg, #000000, #555, #000000 ) !important;   
      */
background-image: -moz-linear-gradient(55deg, #000, #221E1B , #46413E , #221E1B ,#000 ) !important;   
background-attachment:fixed;
background-repeat:no-repeat;  
    
/* 
  background: url('http://images6.alphacoders.com/366/366251.jpg') top center /cover, #222 !important;
  background: url('http://images2.alphacoders.com/468/468357.jpg') top center /cover, #222 !important;
  background: url('http://www.hostpic.org/images/1501021231080089.jpg') top center /cover, #222 !important;
  background: url('http://www.hostpic.org/images/1412232224020091.jpg') top center /cover, #222 !important;
  background: url('http://images8.alphacoders.com/410/410631.jpg') top center /cover, #222 !important;
 */    
  /* background: url('http://www.hostpic.org/images/1501021231080089.jpg') top center /cover, #222 !important;  */
    
}

html a {
	color: #FDBA75 !important;      /*  rgb(0,173,238) */
	text-decoration: none;
	font-weight:lighter;
}
html a:hover {
	color: #A8FFA7   !important;
	text-shadow: 0px 0px 3px rgba(0,173,238,.5);
	text-decoration:underline;
	-moz-transition: .1s ease-in;
}
html a:active, 
html a:focus {
	color: #A8FFA7  !important;
	text-shadow: 0px 0px 3px rgba(0,173,238,.5);
	text-decoration: none;
	outline:none;
	-moz-transition: .1s ease-in;
}

/* to Mozilla link */
#aboutMozilla {
    filter: brightness(4) sepia(100%) opacity(100%) saturate(6000%) hue-rotate(-40deg)  ;
}
    
html > body > div:first-child.spacer {
     max-height: 0px !important;  /* 204px родной */
}  
    
/*  глобус  */
#brandLogo {
}    

    
/* *** весь блок поиска ***************************  */
    
#searchIconAndTextContainer {
    position: relative   !important;
    top: -30px !important;    /* 0px родной */
}
/* иконка слева */
#searchIcon {
    background-image: url('data:image/png;base64,') !important;
    background-size: 30px 30px !important;
    background-color: transparent !important;
    -moz-transition: all  100ms ease  !important;
}
#searchIcon:hover,
#searchIcon[active] {
    background-image: url('data:image/png;base64,') !important;
    background-size: 33px 33px !important;
}
    
#searchIcon:hover,
#searchIcon[active],
#searchIcon[focus]{
}


 
    
/* строка поиска  ******************************     */
#searchText, 
div[class="snippet"][style="display: block;"] >  form > #welcome > p.input > input  {
  height: 24px  ;   /*    кнопка тоже  */
  padding: 3px 6px 3px 36px!important;
  box-shadow: none !important;
  font-size: 1.0em  !important;
  font-weight: bold !important;  
  color: var(--str-c)  !important;
  background: var(--fon-str)  !important;
  border-top-left-radius: 0px !important;
  border-bottom-left-radius: 0px !important;
}
/* кн. Найти  / Послать мне ссылку */
#searchSubmit, 
div[class="snippet"][style="display: block;"] >  form > #welcome > p.input > button {
  background-image: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go")    ,
        linear-gradient(to bottom, rgba(255, 255, 255, 0.4), rgba(255, 255, 255, 0.15)) 
  !important; 
  border-top-right-radius: 0px !important;
  border-bottom-right-radius: 0px !important;    
  box-shadow: none !important;
  min-height: 24px  !important;
  filter: contrast(290%) ; 
}
#searchText, 
div[class="snippet"][style="display: block;"] >  form > #welcome > p.input > input ,    
#searchSubmit, 
div[class="snippet"][style="display: block;"] >  form > #welcome > p.input > button 
{
      border: 1px solid var(--bor-str)  !important;
}

#searchSubmit:hover  ,
div[class="snippet"][style="display: block;"] >  form > #welcome > p.input > button:hover     {
  background-image: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go")    ,
             linear-gradient(to bottom, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.2)) 
  !important; 
  box-shadow:   none !important;  
  filter: contrast(295%) grayscale(100%)  !important;   
  cursor: pointer;
}

    
    
    
    
/* вып. блок выбора поиск.движка ***********************  */
#searchSuggestionTable  {
/*   для настройки:  
    display:  inherit  !important;          */
    
    outline: 1px solid  var(--bor-bl)  !important; 
/* **** фон *************** */ 
    background-color: var(--fon-bl)   !important; 
    margin-left: 1px !important;
}

/* заголовки  Искать в ... / Поиск через: /  Изменить параметры поиска    */ 
#contentSearchDefaultEngineHeader,
#contentSearchSearchWithHeader ,
.contentSearchHeader {
    display:  inherit  !important;
    color: var(--tit)  !important;
    background: transparent !important; 
}
   
/* варианты поиск.запроса */
.contentSearchSuggestionsList {
    border-color:  transparent !important;      
}
.contentSearchSuggestionEntry {
    color: var(--beg-c0) ;
    font-weight: bold !important;  
} 
.contentSearchSuggestionEntry:hover ,
.contentSearchSuggestionEntry[aria-selected="true"]   {
    -moz-appearance: none !important;
    background: transparent  !important; 
    
    color:  var(--beg-c)  !important;
    box-shadow: inset 0 0 0px 1em  var(--beg-bg)  !important; 
}    

/*  блок иконки поиск. систем  */  
table[class="contentSearchOneOffsTable contentSearchSuggestionsContainer"] > tr * {
    border-color:  transparent !important;  
    background: transparent  !important; 
}
table[class="contentSearchOneOffsTable contentSearchSuggestionsContainer"] > tr button {
   border: 2px solid transparent !important; 
   border-radius: 8px !important;
}
/*  иконки поиск. систем  hover  */  
table[class="contentSearchOneOffsTable contentSearchSuggestionsContainer"] > tr button:hover {
/*     box-shadow: inset 0 0 20px 0px var(--beg-bor) !important;   */
   border-color: var(--beg-bor) !important;
}
    
/* Изменить параметры поиска */ 
#contentSearchSettingsButton {
    font-weight: bold !important;   
    height: 30px !important;
}
#contentSearchSettingsButton:hover {
   color:  var(--beg-c)   !important;
   background: var(--beg-bg) !important;    
/*   box-shadow: inset 0 0 0px 30px  var(--beg-bg)  !important;  */
   cursor: pointer  !important;  
}
    
    
    
    

    
/* *********** инфа под строкой поиска *** всякая гадость *****************  */
#snippetContainer ,
div.snippet-metadata    {
/* !!! ЕСЛИ СИЛЬНО НАДОЕЛ  !!!!!!!!!     */
  display: none !important;   
}


    
    
/*  **** нижняя полоса с иконками *************************** */
    
#launcher{
    border-top: 1px solid rgb(0,0,0) !important;
    box-shadow: none !important;
    background-color: rgba(0,0,0, .25) !important;
    background-image: linear-gradient( rgba(0,0,0,.25), rgba(0,0,0,0) 10%) !important;
}
    
/*   иконки-ссылки внизу   */
.launchButton{
    background-color: transparent ;
    border-radius: 17px !important;
    color: var(--light0) !important;  /* D2D2D2  rgb(120,120,120) */
    -moz-transition: 0.4s ease !important;
    margin-left: 10px !important;
    margin-right: 10px !important;
}
.launchButton:hover   {
    background-color: rgba(0,0,0, .5) !important;
    box-shadow: 0px 0px 12px 5px var(--light) !important;
    color: var(--light) !important; /*  rgba(0,173,238,.75) */
    text-shadow: 0px 0px 3px var(--light) !important;
    -moz-transition: .1s ease !important;
}

#aboutMozilla:before{
    content: url("data:image/png;base64,") !important;
}
#downloads:before{
    content: url("data:image/png;base64,") !important;
}
#bookmarks:before{
    content: url("data:image/png;base64,") !important;
}
#history:before{
    content: url("data:image/png;base64,") !important;
}
#apps::before{
    content: url("data:image/png;base64,") !important;
}
#addons:before{
    content: url("data:image/png;base64,") !important;
}
#sync:before{
    content: url("data:image/png;base64,") !important;
}
#settings:before{
    content: url("data:image/png;base64,") !important;
}
body[narrow] #restorePreviousSession::before{
    content: url("data:image/png;base64,") !important;
}
#restorePreviousSession::before{
    content: url("data:image/png;base64,") !important;
}
#restorePreviousSessionSeparator{
background-image: linear-gradient(rgba(255,255,255,0), rgba(255,255,255,.35), rgba(255,255,255,.0)) !important;
}



}  /*  about:home -------------------- */
