@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {







/*Graphiste et codeur: Missgirl58*/



/*FOND*/

#contenu{
  background:url('http://www.zupimages.net/up/16/49/tzkw.png') no-repeat fixed 50%;
  background-color: white;}

/*HEADER*/

#head_forum{
  background:url('http://www.zupimages.net/up/17/01/v6fu.png');}

#head_links a{
  color: white;}

#head_links{
  top: 268px;}

#head_links a:hover{
  color: #6292b4;}

