#brdmenu{
  background:url('http://www.zupimages.net/up/16/49/u0zg.png');
  width: 938px;
  height: 28px;}

#brdmenu A:link{
  color: #6292b4;}

#brdmenu A:hover{
  color: #6292b4;}

/*BRDWELCOME*/

#brdwelcome{
  background:url('http://www.zupimages.net/up/16/49/7gky.png');
  width: 930px;}

#brdwelcome .conl{
  color: white;}

.pun A:link{
  color: #6292b4;}
    
.pun A:visited{
  color: #6292b4;}
    
/*PUNH2*/

.pun H2{
    background:url('http://www.zupimages.net/up/16/49/7abi.png');
    color: #6292b4;}
    
/*PUNTH*/   
  
.pun TH{
    background:url('http://www.zupimages.net/up/16/49/28y6.png');
    color: #6292b4;}
    
/*DIVINEW*/

DIV.inew {
    background-image: url("http://www.zupimages.net/up/16/49/es99.png") !important;
    z-index: 100000;}
    
