DIV.icon {
    background-image: url("http://www.zupimages.net/up/16/49/rjc9.png");}
    
/*FOOTERBOX*/
    
#footer_box{
      background:url('http://www.zupimages.net/up/16/49/8hfp.png');}
    
#footer_box .conr, #footer_box a{
