DIV.box{
      background-color: white;}

.pun .tcr{
      border-right: 2px solid #6292b4;}
    
#announce DIV.inbox DIV{
      color: #6292b4;}
        
TD DIV.tclcon{
      color: white;}
        
.pun{
      color: #6292b4;}
          
.pun .tcr .byuser{
      color: #6292b4;}

TR.iclosed TD.tcl A:link{
      color: #93b3ca;}

/*COMMENTAIRES DES JOUEUSES*/

DIV.postfootright{
      border-color: #b8d6f1;
      border-left: 18em solid #b8d6f1;}

DIV.postfootleft{
      background-color: #6292b4;}

DIV.postright{
      border-color: #6292b4;
      border-left: 18em solid #b8d6f1;}

DIV.postleft DL{
  background-color: #b8d6f1;}

DIV.postright DL{
      background-color: white;}

.pun H2 A:link, .pun H2 A:visited{
      color: #6292b4;}

DIV.blockpost H2 SPAN.conr{
      color: #b8d6f1;}

.pun DIV{
      background-color: #b8cee1;}

TD.tc2, TD.tc3{
      background-color: #b1cbe3;}

.box{
      border: 2px solid #6292b4;}

.pun BLOCKQUOTE{
      background-color: #b1cbe3;
      border: 1px solid #6292b4;}

DIV.linkst .conl, DIV.linksb .conl, DIV.postlinksb .conl, DIV.postlinksb LI{
      color: #6292b4;}

DIV.linkst LI{
      color: #6292b4;}
  
/*POSTERUNCOMMENTAIRE*/
  
.pun LEGEND{
      color: white;}
  
