body {
    overflow-x: hidden;
    max-width: 960px;
    margin: 0 auto;
    padding: 2em 0;
    color: rgba(0, 0, 0, .8);
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-weight: 300;
    font-size: 21px;
    line-height: 1.8;
}

h1,
h2,
h3 {
    color: #000;
    font-weight: 500;
    font-family: inherit;
}

table {
    border-collapse: collapse;
}

th,
td,
table.vert th,
table.vert td {
    border: 1px solid rgba(0, 0, 0, .1);
    padding: 1em 2em;
    background: none;
    line-height: 1.2;
    font-family: inherit;
}

table.vert th {
    background: #359;
    color: #fff;
}

thead,
tbody,
tfoot {
    border: none !important;
}

:link,
:visited {
    text-decoration: none;
    background-image: linear-gradient(to bottom, rgba(0, 0, 0, .3) 50%, rgba(0, 0, 0, 0) 50%);
    background-repeat: repeat-x;
    background-size: 2px .1em;
    background-position: 0 1.07em;
    color: inherit;
}

a {
    border: none !important;
    background-color: transparent !important;
}

a[href]:hover {
    background-image: linear-gradient(to bottom, rgba(0, 0, 0, .5) 50%, rgba(0, 0, 0, 0) 50%);
    background-repeat: repeat-x;
    background-size: 2px .1em;
    background-position: 0 1.07em;
}

a[href]:focus,
a[href]:hover {
    margin-bottom: initial;
}

p {
    margin: 1em 0;
}

strong,
b,
dt,
.tocline1 {
    font-weight: 500 !important;
}

tt,
code,
kbd,
samp,
.idl-code {
    font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
    font-size: 90%;
    font-style: normal;
    color: #79b;
}

var {
    background: none;
    border: none;
    font-family: "PT Serif", Georgia, serif;
}

pre {
    margin-left: 0;
}

.self-link {
    background: none;
}

@media screen and (min-width: 78em) {
    body.toc-sidebar {
                                            padding-left: 21.5em;
                                        }

    body.toc-sidebar :not(li) > .toc {
                                            margin: 0;
                                            font-size: 18px;
                                        }

    body.toc-sidebar #toc li li {
                                            font-size: 90%;
                                        }

    body.toc-sidebar #toc {
                                            width: 16em;
                                        }
}

.toc ol {
    margin-top: .5em;
}

.toc a {
    margin: initial !important;
    border: none !important;
    background: none !important;
}

.toc > li {
    font-weight: 400;
    line-height: 1.4;
}

.toc > li li {
    font-weight: 300;
}

.toc > li,
.toc > li li,
.toc > li li li,
.toc .secno {
    margin-left: 0 !important;
}

.toc .content {
    display: block;
    overflow: hidden;
}

.toc .secno {
    opacity: .8;
    font-size: 90%;
}

.prev_next {
    font-size: 65%;
}

#toc-nav,
#toc-nav a {
    font-size: .8rem;
    border-radius: 0;
    background: #359;
    box-shadow: none;
}

#toc-nav a {
    display: block;
    height: 2em;
    line-height: 2;
}

#toc-nav #toc-jump span,
#toc-nav #toc-toggle span {
    color: #fff;
    vertical-align: middle;
}

#toc-nav > a[href="#toc"]:not(:hover):focus:last-child {
    padding-bottom: .1em;
}

.bug-link {
    padding: .2em 1em !important;
    background: #359 !important;
    color: #fff !important;
    font-size: .8rem;
}

#toc-nav,
.bug-link {
    opacity: .5;
    transition: opacity .3s;
}

#toc-nav:hover,
.bug-link:hover {
    opacity: 1;
}

.element-name,
.prop-name,
.prop-value,
.attr-name,
.attr-value,
.code-fragment,
.idlattr,
.dom-method-name,
.color-keyword-value,
.filterformula,
.SVG-Term {
    font-weight: 500;
    font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
    font-size: 90%;
}

.element-name {
    color: #20af74;
}

.prop-name {
    color: #3090e4;
}

.prop-value {
    color: #4e82c2;
}

.attr-name {
    color: #f39d1c;
}

.attr-value {
    color: #3949ab;
}

pre,
.code-fragment {
    color: #79b;
    font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
    font-size: 90%;
}

table,
blockquote,
pre,
.element-summary,
.note:not(span),
.example,
.issue,
.advisement,
.XXX,
.warning,
dl.domintro {
    background: #fcfcfc;
    border: 1px solid rgba(0, 0, 0, .1) !important;
    border-radius: 3px;
    box-shadow: 0 0 45px rgba(0, 0, 0, .2);
    padding: 1.5em 2em;
    font-size: 75% !important;
}

.example blockquote,
.example pre,
.example .example,
table table {
    font-size: 100% !important;
}

table {
    font-size: 65%;
}

.example table,
.example .domTree {
    font-size: 81.25% !important;
}

.note {
    background-color: #ecfbf0 !important;
}

span.note {
    border-width: 1px;
    border-style: none solid solid none;
}

.example {
    background-color: #fff6bf !important;
}

.example .example {
    background-color: rgba(255, 255, 255, .6) !important;
}

.issue {
    background-color: #fbecec !important;
}

.advisement {
    background-color: #ffe5b0 !important;
}

.XXX {
    border: 3px solid #e66 !important;
}

.warning {
    border: 3px solid #e66 !important;
    margin-right: initial;
    margin-left: initial;
}

pre.def {
    background: #def !important;
}

p.warning:before {
    content: "\26A0  Warning! ";
}

dl.domintro {
    background: #e9fbe9 !important;
}

dl.domintro:before {
    margin: -2.5em -1em 1.5em auto;
    border-radius: 3px;
    box-shadow: 0 0 15px rgba(0, 0, 0, .2);
}

.adef {
    font-size: 75%;
}

.example-ref {
    color: #00897b;
}

.idlattr,
.idlattr:link,
.idlattr:visited {
    background: none;
    color: #0086b3;
}

.dom-method-name {
    background: none;
    color: #e96900;
}

.color-keyword-value {
    color: #999;
    font-weight: 300;
}

.filterformula {
    color: #c53429;
}

.SVG-Term {
    color: #ce2842;
}

.propinfo {
    font-size: 80%;
}

.propdef .descdef-title {
    color: #cb6;
    background: rgba(0, 0, 0, .03);
}

.atrisk::before {
    content: 'Not yet widely implemented';
    margin: 0;
    padding: .2em .3em;
    border-style: solid none;
    font-size: 75%;
    text-indent: initial;
}

body:not(.toc-inline) .overlarge {
    overflow: visible !important;
    margin: auto !important;
}

table.data tbody th:first-child,
table.index tbody th:first-child {
    border-right: 1px solid #c0c0c0;
}

table.index th:first-child a {
    font-weight: 400;
}

table.def td,
table.def th {
    border-style: solid none;
    border-color: rgba(74, 150, 193, 0.2) !important;
}

table.def {
    background-color: #f3f8fb !important;
    border-color: rgba(74, 150, 193, 0.2) !important;
}

.dfn-panel {
    margin-left: .3em;
    padding: .5em 1em;
    border: 1px solid rgba(0, 0, 0, .1);
    background: #fff;
    box-shadow: 0 0 25px rgba(0, 0, 0, .2);
    border-radius: 3px;
}

ul.domTree {
    font-family: inherit;
    font-size: 70%;
    line-height: initial;
}

ul.domTree .t1 code {
    font-weight: 400;
    font-size: 100%;
}

ul.domTree span {
    font-family: "PT Serif", Georgia, serif;
}

.color-keywords td {
    padding: .6em 1em;
}

.color-keywords > tbody > tr > td {
    background: none;
    border: none;
}

.color-keywords > tbody > tr > td:nth-child(2) {
    display: none;
}

.color-keywords table {
    border: none !important;
    box-shadow: none;
}

.color-keywords td td {
    border: none;
}

}
