css:
@-moz-document domain("linux.org.ru") {   
        :root{
        --bg1: #1d2021!important;
        --bg2: #282828!important;
        --bg3: #3c3836!important;
        --bg4: #665c54!important;
        --bg5: #7c6f64!important;
        --fg1: #fbf1c7!important;
        --fg2: #ebdbb2!important;
        --fg3: #d5c4a1!important;
        --fg4: #bdae93!important;
        --reddk: #cc241d!important;
        --redlt: #fb4934!important;
        --orangedk:#d65d0e!important;
        --orangelt:#fe8019!important;
        --greendk: #98971a!important;
        --greenlt: #b8bb26!important;
        --bluelt: #83a598!important;
        --bluedk: #458588!important;
        --purpledk: #b16286!important;
        --purplelt: #d3869b!important;
        --aquadk:#689d6a!important;
        --aqualt: #8ec07c!important;
        --graydk: #928374!important;
        --graylt: #a89984!important;
    }
  body,htmlS{
    color: var(--fg2)!important;
    background-color: var(--bg1)!important;
  }
  #sitetitle, #loginGreating a{
    color: var(--aqualt)!important;
    text-decoration-color: var(--aqualt)!important;
  }
  a{
        color: var(--fg3)!important;
  }
  .tags{
    margin-bottom: 0px!important;
  }
  #hd .menu a,#bd h2,fieldset legend,#bd h1{
    color: var(--fg1)!important;
  }
  #hd .menu a:hover{
    color: var(--aqualt)!important;
  }
  #bd .forum table tbody tr:hover,#bd .forum table tfoot{
    background-color: var(--bg3)!important;
  }
  #bd .btn,#bd .btn-default,#bd .forum table thead,.message-table .tag{
    color: var(--fg1)!important;
    border: none!important;
    background-color: var(--bg4)!important;
  }
  #bd .btn-selected{
    background-color: var(--bg4)!important;
  }
  .boxlet,.infoblock,.code,.messages .msg{
    background-color: var(--bg2)!important;
  }
  .messages .msg:target{
    border-color: #cc241d!important;
  }
  pre code{
    color: var(--graylt)!important;
  }
  pre .built_in,*|*:link{
        color: var(--bluedk)!important;
  }
  #interpage-adv{
   display: none!important; 
  }
}
