css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vkontakte.ru") {a:hover { color: #FFFF33 !important; text-decoration: underline !important}
body {background: url(http://s49.radikal.ru/i124/0901/03/39efce530d15.jpg) fixed left !important; font-family: "Courier New"; color: #669900; font-weight: bold}
#pageHeader {Background: url(http://s60.radikal.ru/i170/0808/01/a412c93baf61.png) no-repeat !important; }
#quickquery {background: black !important; color: #FF9900}
DIV, Select, TD, TEXTAREA, SPAN.ncc, Input, A, B,#endQuote, #startQuote, .actionspro, LI.written, TH, UL.pageList, .actionspro, ul.topNav li, #userProfile, #profileActions, .nc *,.groupslist table,#td.info {background: transparent none !important}
#rateLeft, .voting_result_fg {background: #226F26 !important}
#rateRight, .voting_result_bg {background: #FFFF00 !important}
a:hover, .header, #header, #content { background-image: none !important}
option, #boxWrap {background: #868585 !important}
a, h1, h2 {color: #FF0000 !important}
a:hover, #nav li a:hover {color: #FFCC00 !important;}
#myLink, td, small, caption, span, p, b, LI, Div, #nav li a, input, select, option, dd, label {color: #CC33CC !important; font-weight: bold !important}
#quickquery, div.actions, #myLink, #content, TextArea,Div.playline, TD, Input, select, TH, H4, H2, #photoactions a, #photoactions .action_link, #photoactions .non_action, .bNav li a, ul.buttonRow a,
.header, .flexHeader, #wrapH
{border-color: #3333FF !important}
#wrapH {border-bottom: 1px dashed; border-color: #3333FF !important}
#nav li a, .nc *, LI, p, B, #profileActions a, .pageList a, div
{border-color:transparent !important;}

}
