@-moz-document domain("mail.google.com") {

/* *** VER 1.5 by OscarS *** */
  
/* *** Global *** */
  
  *{ border-radius: 0px !important;
	 text-shadow: none !important;
   font-family: 'Nimbus', sans-serif;
   }

/* *** Elimina todo lo relacionado con Gmail *** */
  
  /* Pantalla de carga inicial */
  .msg{display: none !important;}
  .msgb{display: none !important;}
  
  /* Logo en página de impresión */
  body > div > table:first-child img[src*="/images/logo"],
  body > div > table:first-child b {display: none !important;}
  
  /* Logo de Gmail */
  #gbq1 div a, a.gb_Fb  { display: none !important }
  
  /* Opciones de Google+ y servicios de Google */
  .gb_jc.gb_Ef.gb_R {display: none !important;}
  
  /* Menú Gmail-Contactos-Tareas */
  .aki.pp { display: none !important;}
  
  /* Invitar a... en panel de lectura */
  .Bu.y3 {display: none !important;}
  
  /* Enlaces de pié de página */
  .aeG, .l2.ov, .apO{display: none !important;}
  
  /* Invitación a inbox */
  .TC{display: none !important;}

  /* Mensajes de la vista previa */
  .apb{display: none !important;}
  
  /* Ficha de hangouts del remitente */
  div.o-ms-fk,  
  div.tq iframe {display: none !important;}
 
/* *** Formato de elementos simples *** */
  
  /* Remitente en negrita y coloreado */
  .yP { font-weight: bold !important;
    color: cadetblue !important;}
  .zF {color: steelblue !important;}
  
  /* Divisoria mensajes-previsualizar */
  .apj{ width: 1px !important;}
  
   /* Borde de los mensajes */
  .av, .F, .iY, .v9, .hX{border: none !important;
    border-top: 1px solid #fff !important;}
  
/* *** Formato de elementos complejos *** */
  
  /* *** Cabecera *** */
  .gb_Ee.gb_Ef {background: #6a6f7c !important; border-bottom: none !important;} 
  
  /* Barra de búsqueda */
  #gbqfqw {width: 500px !important;
    border-radius: 20px !important;
    left: 45px !important;
    background: rgba(255,255,255,.15);
    color: rgba(255,255,255,.75);
    border: 0;}
  #gbqfbw {display: none;}
  
  /* Barra de acciones 
  .D.E.G-atb{position: fixed !important;
    border: none !important;
    top: 12px !important; left: 550px !important;
    z-index: 5 !important;
    background: transparent !important;}*/
  
  /* Botones de acción */
  .T-I { border: none !important;
    margin: 0px !important; 
    background:none !important;
    min-width: 0px; }

  /* *** Ménu izquierdo *** */
  .nH.oy8Mbf.nn.aeN{top: -65px; background: #505061 !important; z-index: 5;} 
  .nM{font-size: 95%; top: 15px !important;}                                /* Tamaño de la fuente */
  .r9gPwb{position: relative; top: 10px !important;}                        /* Posición de las carpetas */
  .TO .n0{color: rgba(255,255,255,.4) !important;}                          /* Carpetas sin seleccionar */
  .CJ {color: rgba(255,255,255,.6) !important;}                             /* Más-Menos */
  .ain {border-left: 4px solid rgba(255,255,255,.6) !important;}            /* Marcador carpeta actual */
  .nZ a.n0 {color: rgba(255,255,255,.7) !important;}                        /* Seleccionada */
  .NQ.TO .n0 {color: rgba(0,0,0,.5) !important;}                            /* Hover sobre carpeta */  
  
  /* Botón redactar */
  .T-I.J-J5-Ji.T-I-KE.L3 {background: rgba(255,255,255,.25) !important;
    border: 3px solid rgba(255,255,255,.25) !important;
    border-radius: 20px !important;
    color: rgba(255,255,255,.7) !important;}
  
  /* Mensajes de acciones de Gmail */
  .vh { left: 375px !important; top: -21px !important; 
    background: rgba(255,255,255,.15) !important;
    color: rgba(255,255,255,.7) !important;
    align-content: center !important;
    border: none !important;}
  
  /* Etiquetas condensadas */
  .ar { max-width:10px !important; transition: all 0.2s ease 0.3s !important; }
  .ar .av { opacity:0 !important; transition: all 0.2s ease 0.1s !important;}
  .ar:hover { max-width:250px !important;	transition: all 0.2s ease 0.3s !important;}
  .ar:hover .av { opacity:1 !important; transition: all 0.2s ease 0.3s !important;}

}
