css:
@-moz-document domain("curiouscat.me") {
body {
    background: #333;
    color: #aaa;
}
#webapp {
    background: #333;
}
#topmenu {
    background: #444;
}
#topmenu li.active a {
    color: #D47A00;
}
.navbar-fixed-top {
    background: #444;
    border-bottom: solid 2px;
}
#menuHeader {
    color: white;
    background: #D47A00;
}
#mainmenu li.active {
    border-bottom: 3px solid #D47A00;
}
#mainmenu li.active a {
    color: #FFF;
}
.postcontainer .addressee,
.postcontainer .addressee a {
    color: #D47A00;
}
.socialfeed ul li a {
    color: #D47A00;
}
div.actionButtonContainer {
    background: #555;
}
.profile {
    background: #555;
}
.profile .col-md-4 {
    color: #D47A00;
}
#mainmenu li a {
    color: #D47A00;
}
.postcontainer .author,
.postcontainer .author a {
    color: #D47A00;
}
.postcontainer {
    background: #444;
    border-bottom: 2px solid #888;
}
.postcontainer .comment {
    color: #FFF;
}
.postcontainer .reply {
    color: #aaa;
}
.profile .col-md-8>.topborder {
    box-shadow: -2px 0 0 #888;
}
a {
    color: #D47A00;
}
a:focus,
a:hover {
    color: #D47A00;
    text-decoration: underline;
}
.socialfeed {
    background: #555;
}
.socialfeed ul li.active {
    border-bottom: 3px solid #D47A00!important;
}
.banner {
    border-bottom: 2px solid #D47A00;
}
#configMenu {
    background: #444;
}
#configUl li a {
    color: #aaa;
}
.profile .col-md-4 textarea {
    border: 1px solid #888;
    background: #444;
}
.alert-warning {
    background-color: #555;
    border: 2px solid;
    border-color: #666;
    color: #fff;
}
.postcontainer .react {
    color: #aaa;
    background: #444;
}
.notifications .notificationcontainer {
    border-bottom: 2px solid #888;
    background: #555;
}
.notifications {
    border-top: 2px solid #D47A00;
    background: #555;
    color: #fff;
}
.profile .topborder {
    background: #555;
}
.form-control {
    color: #aaa;
}
.profile .switch {
    background: #666;
}
.btn-secondary {
    color: #fff;
    background-color: #f0ad4e;
    border-color: #f0ad4e;
}
.btn:focus,
.btn:hover {
    color: #fff;
    background-color: #ec971f;
    border-color: #eb9316;
}
.notifications .notificationcontainer .remove span {
    color: #D47A00;
}
.notifications .notificationcontainer .uploadimage span {
    color: #D47A00;
}
.notifications .notificationcontainer .replyform .uploaddiv p {
    color: #D47A00;
}
.form-control:focus {
    color: #aaa;
}
.notifications .notificationcontainer .replyform textarea {
    border: 1px solid #888;
    background: #444;
}
.notifications .notificationcontainer .date {
    color: #aaa;
}
.postcontainer .date a {
    color: #aaa;
}
.profile .followstats a {
    color: #D47A00;
}
.notiffeed ul li a {
    background-color: #555;
}
.notiffeed ul li.active {
    border-bottom: 3px solid #aaa!important;
}
#discussionsfilters {
    background: #555;
    border-bottom: 2px solid #ccc;
}
.discussions>div {
    background: #444;
    border-top: 2px solid #aaa;
}
.discussions .top .timestamp {
    color: #D47A00;
}
.discussions .comments>div a {
    color: #D47A00;
}
.discussions .viewall a {
    color: #D47A00;
}
.discussions input {
    color: #aaa
}
#discussionsfilters .btn-secondary a {
    color: #fff;
}
.discussions .viewall {
    border-top: 2px solid #aaa;
}
#bottomyourd {
    background: #555;
}
#bottomyourd a {
    color: #D47A00;
    background: #555;
}
#discussionsfilters {
    border-bottom: 5px solid #aaa;
}
.discussions input::-webkit-input-placeholder {
    color: #aaa;
}
.jconfirm.jconfirm-material .jconfirm-box {
    background-color: #444;
    border: 3px solid #666;
}
.jconfirm.jconfirm-material .jconfirm-box div.jconfirm-title-c {
    color: #aaa;
}
.followusers>div p {
    color: #D47A00;
}
.followusers .btn-menufollow {
    background-color: transparent;
    border-color: #6F6F6F;
    padding: 3px;
    font-size: 12px;
    position: absolute;
    top: 5px;
    right: 0px;
    color: #D47A00;
}
#yourdiscussions {
    background: #444;
}
::-webkit-scrollbar {
    display: none;
}
#maincontainer {
    background: #555;
}
#singlepostwarning {
    color: #FFF;
    background: #444;
    border: 2px solid #888;
    margin: 0px 0px 0px -2px;
}
.notification {
    background: #444;
}
#inbox .inboxpost {
    background: #444;
}
#inbox .inboxpost:hover {
    background: #444;
}
#discoverMain {
    background-image: none;
    background: #444;
}
#discoverTopics {
    background: #444;
}
#realSearch input {
    background: #555;
    border-bottom: 2px solid #888;
}
#realSearch input textarea {
    color: #fff;
}
}
