css:
@-moz-document regexp("https://.*.slack.com/messages.*") {
body {
    background: rgba(0, 0, 0, 1.0);
    color: #c0c0c0;
}
html * {
    font-family: Consolas, "Andale Mono WT", "Andale Mono", "Lucida Console", "Lucida Sans Typewriter", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Liberation Mono", "Nimbus Mono L", Monaco, "Courier New", Courier, monospace;
}
.timestamp {
    opacity: 1 !important;
    color: #444 !important;
}
ts-message.active:not(.standalone):not(.multi_delete_mode):not(.highlight):not(.new_reply),
ts-message:hover:not(.standalone):not(.multi_delete_mode):not(.highlight):not(.new_reply) {
    background: #0c0c0c;
}
.message.first {
    border-top: 1px dashed #333 !important;
}
.dense_theme .message_content_header,
.dense_theme .message_content_header_left {
    display: block;
    float: left;
    width: 100px;
}
.message_body {
    display: block;
    margin: 0 0 5px 100px;
    max-width: 700px;
    color: #FFF;
}
.dense_theme ts-message .message_content {
    margin-left: 5.5rem;
    padding-left: .5rem;
}
a,
a:link,
a:visited {
    color: #8551FB;
}
a:hover,
a:active,
a:focus {
    color: #D15353;
}
hr {
    border-top: 1px solid rgba(0, 0, 0, 0);
    border-bottom: 1px solid #8551FB;
}
h1,
h2,
h3,
h4,
h1 a,
h1 a:active,
h1 a:hover,
h1 a:link,
h1 a:visited {
    color: #c0c0c0
}
input[type="text"]:focus,
input[type="text"]:active,
input[type="number"]:focus,
input[type="number"]:active,
input[type="email"]:focus,
input[type="email"]:active,
input[type="password"]:focus,
input[type="password"]:active,
textarea:focus,
textarea:active,
select:focus,
select:active {
    box-shadow: 0 0 7px rgba(0, 0, 0, 0.15);
    border-color: #D15353;
}
::-webkit-input-placeholder {
    color: #01FD86!important;
    font-family: Consolas, "Andale Mono WT", "Andale Mono", "Lucida Console", "Lucida Sans Typewriter", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Liberation Mono", "Nimbus Mono L", Monaco, "Courier New", Courier, monospace;
}
::-moz-placeholder {
