css:
@-moz-document domain("discordapp.com") {
.guilds-wrapper,
#voice-connection .btn,
.theme-dark .chat,
.theme-dark .chat>.content,
.theme-dark .chat form .typing,
.theme-dark #friends,
.theme-dark .friends-table,
.theme-dark .friends-table .messages .divider span,
.theme-dark .friends-table .messages .divider:before,
.theme-dark .messages-wrapper,
.theme-dark .messages-wrapper .messages .divider span,
.theme-dark .messages-wrapper .messages .divider:before,
.need-help-modal .header {
    background-image: url(https://mir-cdn.behance.net/v1/rendition/project_modules/source/d7685c43794275.57fd0d92e1cd2.png);
    background-size: contain;
    background-color: #24344b;
    background-repeat: no-repeat;
    background-position: center; 
}

.theme-dark .chat .title-wrap,
.theme-dark #friends .friends-header {
    border-bottom: 1px solid #24344b;
    box-shadow: 0 1px 0 rgba(0,0,0,.06);
    background-image: url(https://mir-cdn.behance.net/v1/rendition/project_modules/source/d7685c43794275.57fd0d92e1cd2.png);
    background-color: #24344b;
}

.theme-dark .friends-table .messages .divider:not(.red) div,
.theme-dark .messages-wrapper .messages .divider:not(.red) div {
	background: #d6c5c5;
    height: 2px;
}

.user-popout .body,
.user-popout .footer,
.theme-dark .search-results-wrap .channel-separator .channel-name {
    background: #24344b;
}

.account .btn,
.account .btn:active,
.theme-dark .channel-members-loading {
    background: #24344b;
}

.theme-dark .friends-table .messages .divider:not(.red) span,
.theme-dark .messages-wrapper .messages .divider:not(.red) span {
    color: #24344b;
}

.scroller-wrap .scroller::-webkit-scrollbar {
    width: 13px;
}

.theme-dark .friends-table .scroller-wrap ::-webkit-scrollbar-thumb,
.theme-dark .messages-wrapper .scroller-wrap ::-webkit-scrollbar-thumb {
    background-color: #24344b;
    border-color: #24344b;
}

.theme-dark .friends-table .scroller-wrap ::-webkit-scrollbar-track-piece,
.theme-dark .messages-wrapper .scroller-wrap ::-webkit-scrollbar-track-piece {
    background-color: #24344b;
    border-color: #24344b;
}

.theme-dark .channel-members {
    background-color: #24344b;
    margin-left: 3px;
    border-left: 1px solid #24344b;
}

.theme-dark .channel-members .member.popout-open,
.theme-dark .channel-members .member:hover {
    background: #24344b;
}

.scroller-wrap.dark .scroller::-webkit-scrollbar-thumb {
    background-color: #24344b;
    border-color: rgba(0, 0, 0, 0);
}

.user-popout .footer {
    border-top: 1px solid #24344b;
}

.user-popout .body .section .label {
    color: #FDF6E3;
}

.member-roles .member-role {
    background-color: #24344b;
    border: 1px solid #1e354a;
    color: #afa8ae;
}

.tooltip {
    background-color: #24344b;
    color: #afa8ae;
}

.tooltip.tooltip-top:after {
    border-top-color: #24344b;
}

.tooltip.tooltip-bottom:after {
    border-bottom-color: #24344b;
}

.tooltip.tooltip-right:after {
    border-right-color: #24344b;
}

.emoji-picker {
    background-color: #24344b !important;
    border: 1px solid rgba(191, 191, 191, 0.2);
    box-shadow: 0 0 6px 0 rgba(0,0,0,.1);
}

.emoji-picker .category {
    background-color: #24344b !important;
    color: #afa8ae;
}

.emoji-picker .categories .item.selected {
    border-bottom-color: #afa8ae;
}

.search-bar.search-bar-light {
    background-color: #24344b !important;
}

.search-bar.search-bar-light .search-bar-inner {
    background-color: rgba(255, 255, 255, 0.0980392);
}

.search-bar.search-bar-light input::-webkit-input-placeholder {
    color: #afa8ae !important;
}

.emoji-picker .scroller-wrap.fade .scroller::-webkit-scrollbar-thumb {
    background-color: #24344b !important;
    border-color: #24344b !important;
}

.emoji-picker .scroller-wrap.fade .scroller::-webkit-scrollbar-track-piece,
.emoji-picker .scroller-wrap.fade:hover .scroller::-webkit-scrollbar-track-piece{
    background-color: #24344b !important;
    border-color: #24344b !important;
    border-radius: 0;
}


.option-popout .btn-item {
    color: #cac6be;
}

.option-popout .btn-item:hover {
    color: #fffefe;
}

.theme-dark .themed-popout {
    background-color: #3c303a;
    border: 1px solid #4d394b;
}

.theme-dark .themed-popout .header {
    background-color: #3c303a;
}

.theme-dark .messages-popout-wrap .messages-popout .message-group {
    border: 1px solid #4d394b;
    border-radius: 1px;
    background-color: #3c303a;
}

.theme-dark .messages-popout-wrap .messages-popout .message-group:hover {
    border: 1px solid #5f485c;
}

.theme-dark .messages-popout-wrap .messages-popout .message-group:hover .action-buttons,
.theme-dark .messages-popout-wrap .messages-popout .message-group .action-buttons .jump-button {
    box-shadow: none;
    background-color: #5f485c;
}

.theme-dark .themed-popout .header .subtitle, .theme-dark .themed-popout .text {
    color: rgba(255, 255, 255, 0.8);
}

.chat .empty-message h1 {
    color: #c3c1c1;
}

.chat .new-messages-bar {
    background-color: rgba(130, 130, 130, 0.8);
}

.theme-dark.context-menu,
.theme-dark.context-menu .context-menu {
    background: #3e313c;
    box-shadow: none;
    border: 1px solid #24344b;
}

.theme-dark.context-menu .item:hover {
    background: rgba(0, 0, 0, 0.1);
    color: #fff;
}

.checkbox .checkbox-inner input[type=checkbox]:checked+span {
    background-color: #24344b;
    border-color: #24344b;
}

.bot-tag {
    background: #a08db9;
}

.guild-header header,
.guild-channels,
.theme-dark .search-results-wrap,
.theme-dark .search-results-wrap .search-header {
    background-color: #24344b;
    box-shadow: 0 1px 0 #24344b, inset 0 -1px 0 rgba(0,0,0,.1);
}

.guild-header header:hover,
.guild-header.popout-open header {
    background-color: #24344b;
}

.guild-channels .channel-text:hover,
.private-channels .channel:hover {
    background: rgba(255, 255, 255, 0.01);
}

.guild-channels .channel-text.selected,
.private-channels .channel.selected,
.private-channels .search-result.selected,
.private-channels .search-result:hover {
    background: #273d51;
}

.guild-channels .channel-text.selected:before,
.guild-channels .channel-text:hover.channel-muted:before,
.guild-channels .channel-text:hover:not(.unread):before,
.private-channels .channel.selected:before {
    border-left: 2px solid #4ecccc;
}

.guild.selected .guild-inner,
.guild.active .guild-inner {
    background-color: #ad93aa !important;
}

#voice-connection {
    background: #24344b;
    border-top: 2px solid #24344b;
    border-bottom: 1px solid #24344b;
}

.slider-bar-fill {
    background: #24344b;
}

.account,
.small-popout-box {
    background: #24344b;
    border: 0;
    border-top: 1px solid #24344b;
}

.account .btn-deafen, .account .btn-mute,
#voice-connection .btn-info {
    border-right: 1px solid #24344b;
}

.account .btn-group,
#voice-connection .btn-group {
    border: 1px solid #1e354a;
}

.private-channels,
.search-bar {
    background-color: #24344b;
    box-shadow: 0 1px 0 #24344b, inset 0 -1px 0 rgba(0,0,0,.1);
}

.private-channels .channel.selected .icon-friends {
    background-color: #808080 !important;
}

.theme-dark #friends .friends-header .tab-bar .tab-bar-item.tab-bar-item-primary {
    background-color: #8b768e;
    color: #fff;
}

.theme-dark #friends .friends-header .tab-bar .tab-bar-item.tab-bar-item-primary:hover:not(.selected) {
    background-color: #958298;
}

.theme-dark #friends .friends-table .friends-row:hover {
    background: #24344b;
    border: 0;
    border-radius: 1px;
    box-shadow: none;
}

.theme-dark .search-results-wrap .search-result:before {
    background-image: -webkit-linear-gradient(bottom,rgba(46,49,54,0),#273d51);
    border-radius: 5px;
}

.theme-dark .search-results-wrap .search-result:after {
    background-image: -webkit-linear-gradient(top,rgba(46,49,54,0),#273d51);
    border-radius: 5px;
}

.theme-dark .search-results-wrap .search-result .search-result-message.hit, .theme-dark .search-results-wrap .search-result.expanded {
    border: 1px solid #273d51;
}

.theme-dark .search-results-wrap .search-result .hit {
    background-color: #3e313c;
    box-shadow: 0 0 10px 6px #273d51;
}

.theme-dark .search-results-wrap .scroller-wrap .scroller::-webkit-scrollbar-thumb {
    background-color: #273d51!important;
    border: 0!important;
}

.theme-dark .search-results-wrap .scroller-wrap .scroller::-webkit-scrollbar-track-piece {
    background-color: #3e313c!important;
}

.theme-dark .search-results-wrap .scroller-wrap .scroller::-webkit-scrollbar {
    width: 10px;
}

.theme-dark.context-menu .item-subMenu {
    background: url(https://discordapp.com/assets/e4afe52f6863408a35654a6e5fd69ba9.svg) 155px 50% no-repeat rgba(0, 0, 0, 0.1);
}

.theme-dark.context-menu .item-subMenu:hover {
    background: url(https://discordapp.com/assets/e4afe52f6863408a35654a6e5fd69ba9.svg) 155px 50% no-repeat rgba(0, 0, 0, 0.2);
}
}
