@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*
           Author: ErraticFox
           Contact: http://twitter.com/erraticfox

           Name: Simple Rounded
           Compatibility: FF 50.0.2
*/


/* custom vars */
/* var(--secondary-text) */
:root {
  --font-fam: "Gotham Rounded", "Lucida Console", "Courier New" !important;
  --red: #F67E7D;
  --blue: #9BA2CC;
} 


/* TABS */

#TabsToolbar:not([collapsed="true"]) + #nav-bar {
  border-top: 0px solid hsla(0,0%,0%,.3) !important;
}

#tabbrowser-tabs {
  background: #fff !important;
  text-align: center !important;
}


.tab-content {
    text-overflow: clip !important;
}

.tab-icon-image, .tab-icon-sound, 
.tab-icon-overlay, .tab-sharing-icon-overlay, 
.tab-throbber, .tab-throbber {
  display: none;
}

#TabsToolbar {
  font-family: var(--font-fam);
  margin-left: -15px !important;
}

#navigator-toolbox::after { 
  height: 0px;
}


#TabsToolbar .tabbrowser-tab[selected] {
  background: #fff !important;
  margin-bottom: 0px !important;
}

#TabsToolbar .tabbrowser-tab {
  background: #F2F2F2 !important;
}

.tabbrowser-tab[fadein]:not([pinned]) { 
  min-width: 100px !important; 
  max-width: 100% !important; 
}

#TabsToolbar {
  border: none !important;
}


.tabs-newtab-button, .tabs-newtab-button:hover { 
  display: block !important; 
  background: #F2F2F2 !important; 
  margin-left: 0px !important; 
  width: 30px !important; 
}


.tabs-newtab-button .toolbarbutton-icon {
  display: none !important;
}


.tabs-newtab-button::before { 
  content: "+" !important; 
}


/* Removes Close Tab Button */
.tab-close-button.close-icon {
  display: none !important;
}

/* Removes Australis Tabs */
.tabs-newtab-button:hover {
  background: #F2F2F2 !important;
}
.tab-background {
  background: transparent !important;
}
.tab-background-start,
.tab-background-end {
  background: transparent !important; 
  background-image: none !important;
}
.tab-background-middle {
  background: transparent !important;
  -moz-box-flex: 1;
}
.tab-background-start::before,  .tab-background-end::before {
  background-image: none !important;
}
.tab-background-start[selected=true]:-moz-locale-dir(ltr)::after,
.tab-background-end[selected=true]:-moz-locale-dir(ltr)::after {
  background-image: none!important;
}

/* hide favicons on unpinned tabs (none or inherit) */
.tabbrowser-tab:not([pinned]) .tab-icon-image {
    display: none !important;     
}

/* removes seperators between pinned tabs */
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}

/* PINNED TABS */

.tab-content, .tab-icon-sound {
  background: none !important;
  background-image: none !important;
}


/* TOOLBAR / URL BAR */

#private-browsing-indicator {
  display: none !important;
}

#nav-bar {
  background: #fff !important;
}

#urlbar {
  font-family: var(--font-fam);
  height: 30px;
  border: none;
}

#urlbar-container {
  text-align: center;
}

#alltabs-button, #find-button, #developer-button, #sync-button, #preferences-button, #add-ons-button, #new-window-button, #fullscreen-button, #history-panelmenu, #save-page-button, #print-button, #privatebrowsing-button, #back-button, #forward-button, #back-button *, #forward-button *, #identity-box, #urlbar-reload-button, #urlbar-stop-button, #urlbar-go-button, #PanelUI-button, #bookmarks-menu-button, #downloads-button, #home-button, #stylish-toolbar-button, #abp-toolbarbutton, #notification-popup-box, #new-tab-button, #private-browsing-indicator, #search-container, #nav-bar-overflow-button, .scrollbutton-up, .tab-close-button, .tab-close, .scrollbutton-down, .tab-throbber, .tab-background-start, .tab-background-end, #pocket-button, #loop-button, #social-share-button, #window-controls, .tab-icon-image:not([pinned]) {
    display: none !important;
}


/* Auto Complete List */

#PopupAutoCompleteRichResult {
  border: 0px !important;
}

#PopupAutoCompleteRichResult * {
  font-family: var(--font-fam) !important;
  font-size: 16px !important;
}

.ac-url-text, .ac-separator {
  display: none !important;
}

.autocomplete-richlistbox {
  padding: 0px !important;
  border: 0px !important;
  margin: 0px !important;
}

.ac-type-icon, .ac-site-icon {
  display: none !important; 
}

.autocomplete-richlistitem {
  border-radius: 0px !important;
  padding: 0 50px 0 50px !important;
  line-height: 1px !important;
}

.autocomplete-richlistitem[selected=true] {
  background-color: var(--red) !important;
}

/* Removes Addon Bar && Personal Bar && Custom Toolbars */ 
#addon-bar, #PersonalToolbar, #customToolbars {
  display: none !important;
}

/* CONTEXT MENU */
#tabContextMenu *, #contentAreaContextMenu * {
  padding: 5px 0px 5px 0px !important;
  font-family: var(--font-fam) !important;
  font-size: 14px;
}

/* Removes Context Icons */
#context-navigation, #context-sep-navigation, #page-menu-separator, .menu-iconic-icon {
  display: none !important;
}
