css:
@-moz-document url(https://kitsu.io/) {

/*
 * Name:    Kitsu Dark
 * Author:  AlenasSenpai
 * Version: v1.4
 * Latest update: 27/12/2016
 * Changelog: additional styling for some things and site include changed as kitsu is no longer closed beta

 */


.add-content-header span[style] {
    color: #f75239;
}

.browse-page .global-container {
    background: #2d2d2d;
    padding-top: 50px;
}

.button--primary {
    background: #f75239;
    color: #e5e5e5;
}

.button--primary:hover {
    background: #DB2409;
}

.card {
    background: #2d2d2d;
    color: #e5e5e5;
}

.card, .hint-panel, .stream-add-content {
    background: #2d2d2d;
}

.card, .hint-panel, .stream-add-content textarea {
    background: #2d2d2d;
    border: 1px solid #f75239;
    border-radius: 5px;
}

.cover-nav .nav-item {
    background: #f75239;
    border: none;
    border-radius: 0 0 5px 5px;
    margin-right: 5px;
}

.cover-nav .nav-item:hover {
    background: #DB2409;
    text-decoration: underline;
}

.cover-page .transparent .logo g, .cover-page .transparent .navbar-buttons li svg path {
}

.custom-control-description {
    color: #e5e5e5;
}

.dropdown-menu {
    background: #2d2d2d;
    border: 1px solid #f75239;
}

.ember-application {
    background: #1d1d1d;
}

.ember-view h6 {
    color: #e5e5e5;
}

.favorite-series-panel .button--primary {
    background: #e5e5e5;
    border: none;
    color: #f75239;
    font-size: 0.9em;
    padding: 3% 0%;
}

.favorite-series-panel .button--primary:hover {
    background: #f75239;
    border: none;
    color: #e5e5e5;
}

.favorite-series-panel .nav .active {
    background: #f75239;
    border: 1px solid #f75239;
    color: #e5e5e5;
}

.favorite-series-panel .nav .nav-link:hover {
    background: #f75239;
    border: 1px solid #f75239;
    color: #e5e5e5;
}

.favorite-series-panel .nav-tabs {
    border-bottom: 1px solid #f75239;
}

.feed-actions a {
    color: #e5e5e5;
}

.feed-actions a.active {
    border: 1px solid #f75239;
    color: #f75239;
}

.feed-actions a.active:hover {
    background: #f75239;
    color: #e5e5e5;
}

.feed-actions a:hover {
    color: #f75239;
}

.feed-actions button {
    background: #f75239;
    border: 1px solid #f75239;
    color: #e5e5e5;
}

.form-control {
    color: #e5e5e5;
}

.kitsu-forums {
    list-style-type: none;
    margin-left: -20px;
}

.kitsu-forums li:before {
    color: #f75239;
    content: "â€¢";
    padding-right: 5px;
}

.media-browse .box-select .active path {
    fill: #f75239;
    opacity: 1;
}

.media-browse .box-select .active:hover path {
    fill: #e5e5e5;
    opacity: 1;
}

.media-browse .box-select li {
    background: #f75239;
}

.media-browse .box-select li.active {
    background: #e5e5e5;
}

.media-browse .box-select li.active:hover {
    background: #f75239;
    opacity: 1;
}

.media-browse .box-select li:hover {
    background: #e5e5e5;
}

.media-browse .box-select li:hover path {
    fill: #f75239;
    opacity: 1;
}

.media-browse .box-select path {
    fill: #e5e5e5;
    opacity: 1;
}

.media-browse .category-select li .exclude-genre svg {
    fill: #e5e5e5;
}

.media-browse .category-select li .exclude-genre svg:hover {
    fill: #f66;
}

.media-browse .category-select li a {
    background: none;
}

.media-browse .category-select li a:hover {
    background: #f75239;
    color: #e5e5e5;
}

.media-browse .category-select li.active a {
    background: #2d2d2d;
}

.media-browse .filter-header .filter-title {
    color: #e5e5e5;
}

.media-browse .filter-header .filter-values {
    background: #f75239;
    color: #e5e5e5;
}

.media-browse .filter-legend {
    color: #e5e5e5;
}

.media-browse .filter-legend svg {
    fill: #e5e5e5;
}

.media-browse .filter-options {
    background: #1d1d1d;
    border-left: 1px solid #f75239;
}

.media-browse .filter-widget {
    border-bottom: 1px solid #2d2d2d;
}

.media-browse .media-grid {
    background: #2d2d2d;
    border-right: 1px solid #f75239;
    padding-top: 4px;
}

.media-browse .media-grid .media-posters {
    background: #2d2d2d;
    padding: 75px 13px 75px 267px;
}

.media-browse .media-grid .poster-wrapper .media-title {
    color: #e5e5e5;
}

.media-browse .media-poster-header {
    background: none;
}

.media-browse .media-poster-header .filter-container {
    background: #1d1d1d;
    border: 1px solid #f75239;
    border-radius: 5px;
    margin-left: -13px;
    margin-top: 10px;
    padding: 1% 0%;
}

.media-browse .media-poster-header .search-media svg {
    fill: #e5e5e5;
}

.media-browse .noUi-background, .media-browse .noUi-base {
    background: #2d2d2d;
    border-radius: 10px;
}

.media-browse .noUi-handle, .noUi-connect {
    background: #f75239;
}

.media-browse .noUi-handle:after {
    background: #2d2d2d;
}

.media-browse .rating-select li {
    background: #f75239;
    color: #e5e5e5;
}

.media-browse .rating-select li.active {
    background: #e5e5e5;
    color: #f75239;
}

.media-browse .rating-select li.active:hover {
    background: #f75239;
    color: #e5e5e5;
}

.media-browse .rating-select li:hover {
    background: #e5e5e5;
    color: #f75239;
}

.media-poster-header, .media-browse .filter-options {
    top: 50px;
}

.navbar {
    background: #1d1d1d;
}

.navbar-light .navbar-brand {
    background: none;
}

.navbar-light .navbar-brand, .navbar-light .navbar-brand:focus, .navbar-light .navbar-brand:hover {
    background: #e5e5e5;
    border-radius: 4px;
    padding: 2% 8%;
}

.navbar-light .navbar-nav .nav-link {
    color: #e5e5e5;
}

.quick-update {
    background: #2d2d2d;
}

.quick-update-actions {
    border-radius: 6px;
}

.quick-update-item {
    animation-delay: 0s;
    border-radius: 8px;
}

.step {
    color: #f66;
}

.step-status circle {
    fill: #f66;
}

.step.step--complete {
    color: #4CAF50;
}

.step.step--complete circle {
    fill: #e5e5e5;
}

.stream-content-editor {
    color: #e5e5e5;
    margin: 0% 2%;
    padding: 0 10px;
    width: 96%;
}

.tag-media {
    border: 1px solid #f75239;
    border-radius: 5px;
    color: #e5e5e5;
    margin: 0% 2%;
    width: 96%;
}

.tag-media .form-control {
    background: #1d1d1d;
    border-radius: 5px;
}

.tag-media .tag {
    border: 1px solid #f75239;
    color: #e5e5e5;
}

.tag-media .tag:hover {
    background: #f75239;
    border: 1px solid #f75239;
    color: #e5e5e5;
}

.tag-media ul li {
    background: #2d2d2d;
    border-radius: 5px;
    border-top: 1px solid #f75239
;
}
ul li a {
    color: #f75239;
    text-decoration: none;
}

ul li a:hover {
    color: #DB2409;
}

/* Scrollbar */

::-webkit-resizer {
    display: none;
}

::-webkit-scrollbar {
    height: 6px;
    width: 6px;
}

::-webkit-scrollbar-button {
    display: none;
}

::-webkit-scrollbar-corner {
    display: none;
}

::-webkit-scrollbar-thumb {
    background-color: #f75239;
    border-radius: 10px;
}

::-webkit-scrollbar-track {
    background-color: #111111;
    width: 5px;
}

::-webkit-scrollbar-track-piece {
    display: none;
}

/* dec 27 update > content load error and feed changes*/
.stream-item {
    background: #3d3d3d;
    border: none;
    border-radius: 6px;
    color: #e5e5e5;
}

.stream-error-wrapper {
    background: #f75239;
    padding: 10px 0px;
    border: none;
    border-radius: 6px;
    box-shadow: none;
    color: #e5e5e5;
    font-size: 1.2em;
}

.stream-item--title-block {
    background: none;
}

.aggregate-wrapper .hr {
    background: #f75239;
}

.aggregate-date {
    background: #f75239;
    color: #e5e5e5;
}

}
