css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dealabs.com") {

  
  body {
    overflow-x: auto;
    margin: 0px;
    padding: 0px;
    font-family: Arial, sans-serif;
    background-color: #333;
    font-size: 0.8em;
}
  
  #menu_white_background {
    width: 100%;
    float: left;
    height: auto;
    background-color: #252525;
    border-bottom: 1px solid #444;
}
  
  .structure.white {
    background: #252525;
}
  
  #contener_type .button_type_deal {
    padding: 5px 15px 2px 15px;
    border-bottom: 3px solid white;
    line-height: 30px;
    margin: 0;
    float: left;
    color: #ccc;
}
  
  
  article.deal_index_article {
    padding: 0px 20px 0px 0px;
    overflow: hidden;
    margin-bottom: 15px;
    background-color: #222;
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    -o-border-radius: 8px;
    -ms-border-radius: 8px;
    -khtml-border-radius: 8px;
    border-radius: 8px;
    border: 1px solid #111;
}
  
  article.deal_index_article .content_part a.title {
    float: left;
    width: 100%;
    display: block;
    font-size: 1.2em;
    font-weight: bold;
    color: #ccc;
    margin-top: 10px;
}
  
  article.deal_index_article .image_part div.price-element p.price span.original_price {
    color: #aaa;
    font-size: 0.9em;
    font-style: normal;
    text-align: center;
    line-height: 12px;
    float: left;
    width: 100%;
}
  
  
  
  
  .body_pagination {
    float: left;
    padding: 15px 0px;
    width: 100%;
    border-top: 1px solid #444;
    background: #252525;
    height: 32px;
}
  footer #footer_merchant {
    width: 100%;
    float: left;
    background-color: #252525;
    border-top: 1px solid #444;
}
  footer #footer_merchant p.top_merchant {
    color: #ccc;
    font-size: 1em;
    text-align: left;
    font-weight: bold;
    margin-top: 12px;
    margin-bottom: 12px;
}
  
  footer #footer_links {
    position: relative;
    width: 100%;
    float: left;
    background-color: #111;
}
  
  footer #footer_links p.title {
    color: #ccc;
    font-size: 1.2em;
    text-align: left;
    font-weight: bold;
    margin-top: 50px;
    margin-bottom: 20px;
}
  
footer #footer_links .statistiques_part p.stats span {
    font-size: 1.1em;
    color: #bbb;
    font-weight: bold;
} 
  
  
  #floatant_title {
    position: fixed;
    width: 100%;
    float: left;
    background: #151515;
    height: auto;
    top: 0px;
    z-index: 998;
    border-bottom: 1px solid #444;
    visibility: hidden;
    opacity: 0;
}
#floatant_title .title_part {
    overflow: hidden;
    margin: 10px 0px;
  color : #ccc;
}  
#floatant_title .title_part p.info {
    font-size: 0.9em;
    line-height: 25px;
    color: #aaa;
}  
  
  article#deal_details .deal_content .detail_part .profil_part {
    float: left;
    width: 100%;
    box-sizing: border-box;
    background-color: #252525;
    border-top: 4px solid rgba(96, 179, 92, 1);
    margin-bottom: 20px;
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    -o-border-radius: 8px;
    -ms-border-radius: 8px;
    -khtml-border-radius: 8px;
    border-radius: 8px;
    border: 1px solid #444;
}
  
 article#deal_details .deal_content .deal_detail_deal_part {
    overflow: hidden;
    border-top: 4px solid #02a5c1;
    background-color: #252525;
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    -o-border-radius: 8px;
    -ms-border-radius: 8px;
    -khtml-border-radius: 8px;
    border-radius: 8px;
    border: 1px solid #444;
} 
  
  article#deal_details .deal_content .deal_detail_deal_part .deal_title_part .title_part h1 {
    float: left;
    width: 100%;
    display: block;
    font-size: 1.3em;
    font-weight: bold;
    color: #ccc;
    line-height: 20px;
    margin-bottom: 10px;
}
  
 article#deal_details .deal_content .deal_detail_deal_part .deal_content_part {
    overflow: hidden;
    background-color: #252525;
    padding: 20px;
} 
  
article#deal_details .deal_content .deal_detail_deal_part .deal_content_part .content_part div.info_sup_div .info_sup {
    margin-bottom: 0px;
    color: #ccc;
    float: left;
    width: auto;
    line-height: 18px;
    margin-bottom: 20px;
}  
article#deal_details .deal_content .deal_detail_deal_part .deal_content_part .content_part p.description {
    float: left;
    width: 100%;
    font-size: 1em;
    color: #aaa;
}  
  
 article#deal_details .deal_content .deal_detail_deal_part .deal_content_part .content_part div.maj_div {
    padding: 0px 10px;
    border: 1px solid #444;
    background: #151515;
    overflow: hidden;
    line-height: 30px;
    margin-bottom: 20px;
} 
 article#deal_details .deal_content .deal_detail_deal_part .deal_footer_part {
    overflow: hidden;
    background-color: #252525;
    border-top: 1px solid #444;
} 
 
  
  
  
  article#deal_details .comment_content #comment_contener .padding_comment_contener {
    padding: 15px;
    width: 100%;
    float: left;
    border-bottom: 1px solid #444;
    background-color: #252525;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -o-box-sizing: border-box;
    -ms-box-sizing: border-box;
    box-sizing: border-box;
}
  
  article#deal_details .comment_content #comment_contener .comment_text_part .commentaire_div {
    overflow: hidden;
    min-height: 66px;
    word-wrap: break-word;
    color : #ccc;
}
  
 div.quote {
    margin: 5px 0px;
    overflow: hidden;
    border-left: 3px solid #444;
    padding-left: 12px;
    padding-right: 12px;
    background-color: #151515;
} 
  
article#deal_details .comment_content #discussed #header_add_commentaire_contener {
    padding: 20px 20px 10px 20px;
    float: left;
    width: 100%;
    box-sizing: border-box;
    background-color: #252525;
}  
article#deal_details .comment_content #discussed #header_add_commentaire_contener .comment_option p.commentaire_text {
    float: left;
    color: #ccc;
    font-size: 1.3em;
    font-weight: bold;
}  
article#deal_details .comment_content #discussed #add_comment_contener {
    padding: 10px 20px 20px 20px;
    background: #252525;
    float: left;
    width: 100%;
    box-sizing: border-box;
}  
article#deal_details .comment_content .comment_text_part_textarea textarea {
    outline: none;
    overflow: auto;
    -moz-border-top-left-radius: 8px;
    -webkit-border-top-left-radius: 8px;
    -o-border-top-left-radius: 8px;
    -ms-border-top-left-radius: 8px;
    -khtml-border-top-left-radius: 8px;
    border-top-left-radius: 8px;
    -moz-border-top-right-radius: 8px;
    -webkit-border-top-right-radius: 8px;
    -o-border-top-right-radius: 8px;
    -ms-border-top-right-radius: 8px;
    -khtml-border-top-right-radius: 8px;
    border-top-right-radius: 8px;
    padding: 9px;
    margin: 0;
    border: 1px solid #444;
    width: 100%;
    overflow: auto;
    min-height: 140px;
    resize: vertical;
    display: block;
    box-sizing: border-box;
  background-color: #252525;
  color:#aaa;
}  
  article#deal_details .comment_content .footer_textarea {
    float: left;
    width: 100%;
    padding: 10px 10px 10px 10px;
    background-color: #151515;
    border: 1px solid #444;
    border-top: none;
    box-sizing: border-box;
    -moz-border-bottom-left-radius: 8px;
    -webkit-border-bottom-left-radius: 8px;
    -o-border-bottom-left-radius: 8px;
    -ms-border-bottom-left-radius: 8px;
    -khtml-border-bottom-left-radius: 8px;
    border-bottom-left-radius: 8px;
    -moz-border-bottom-right-radius: 8px;
    -webkit-border-bottom-right-radius: 8px;
    -o-border-bottom-right-radius: 8px;
    -ms-border-bottom-right-radius: 8px;
    -khtml-border-bottom-right-radius: 8px;
    border-bottom-right-radius: 8px;
}
 
  
  
  
  
 .bloc_rightbar div.padding_div_liste_widget {
    overflow: hidden;
    background: #151515;
    padding: 2px 10px 2px 10px;
} 
  
  .bloc_rightbar div.div_liste_widget, .bloc_rightbar div.div_liste_alert {
    float: left;
    width: 100%;
    background: #151515;
    display: none;
}
  .text_color_333333 {
    color: #ccc !important;
}
.text_color_777777 {
    color: #aaa !important;
}  
  
  
}
