css:
@-moz-document domain("trello.com") {
body {
    background: #fffffd !important;
    color: #999 !important;
    font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
    font-weight: 300;
}
input {
    color: #686868;
}
textarea {
    color: #999;
}
/* h3 {font-weight:300 !important} */

.header-woof {
    display: none !important
}
/* All header */

#header {
    background: #595959 !important;
    /*     padding: 10px !important; */
    box-shadow: none !important;
}
/* Board header  */

.board-header {
    height: 40px !important;
    background: #fffffd !important;
    /*     margin-bottom:20px !important; */
    
    padding-bottom: 0px !important;
}
.board-header-btn-name-org-logo {
    -webkit-filter: contrast(3000%) invert(100%);
}
/* header buttons */

.header-btn,
.header-btn.header-notifications,
.header-btn.header-boards,
.header-btn.header-search,
.header-btn.header-login,
.header-btn.header-signup {
    background: rgba(255,255,255,.1);
    border-radius: 0px !important;
}
.icon-lg.light,
.icon-lg.light-hover:hover,
.icon-sm.light,
.icon-sm.light-hover:hover,
.light-hover:hover .icon-lg,
.light-hover:hover .icon-sm {
    color: #c9c9c9;
}
.header-btn:hover {
    background: rgba(255,255,255,.2)
}
.header-logo {
    opacity: 0;
}
.header-logo:hover {
    opacity: 1;
}
input[type=text].header-search-input {
	background: rgba(255,255,255,.1)
}
input[type=text].header-search-input:hover {
    background: rgba(255, 255, 255,.2);
}
.header-search-icon-dark {
    color: #c9c9c9 !important;
}
.header-search-icon-dark.header-search-icon-close:hover,
.header-search-icon-dark.header-search-icon-open:hover {
    color: #a5a5a5 !important;
}
/* Board header buttons  */

.board-header-btn {
    color: #787878;
    border: 1px solid #fffffd;
    border-radius: 0px !important;
}
.board-header-btn-text {
    text-decoration: none !important;
}
.board-header-btn-name {
    font-weight: 100 !important;
    font-size: 14px;
}
.board-header-btn:not(.no-edit):hover {
    background: none;
    border: 1px solid #c9c9c9;
}
.board-header-btn:hover {
    color: #787878;
}
.board-header-btn-icon {
    color: #787878;
}
.board-header-btn.board-header-btn-enabled {
    background: none;
}
.board-header-btn.board-header-btn-enabled .board-header-btn-icon.icon-star {
    color: #ffee59
}
/* Boards drawer  */

.boards-drawer.is-shown {
    height: 100% !important;
    box-shadow: 0 3px 3px rgba(0, 0, 0, .2);
    top: 0px;
}
.boards-drawer-pinned {
    top: 50px !important;
}
.boards-drawer-header {
    background: #f7f7f6;
}
.board-drawer-content {
    max-height: none !important;
    height: 100% !important;
}
/* Dark button hover  */

.sidebar-section-header-toggle-icon:hover {
    background: #f7f7f6;
}
.quiet-button {
    text-decoration: none;
}
.quiet-button:hover {
    background: #f9f9f9;
}
/* Boards drawer - individual boards  */

.js-open-board {
    background: #fff !important;
    font-weight: 200 !important;
    border: 1px solid #f9f9f9;
}
.js-open-board:hover {
    background: #f7f7f6 !important;
    border-color: #c9c9c9;
}
.js-recent-boards {
    border-bottom: 1px solid #c9c9c9;
}
.js-all-boards {
    border-bottom: 1px solid #c9c9c9;
}
/* Search Pop up  */
.pop-over {
	border-radius: 0px;
}
.pop-over-content {
    background: #fff;
}
/* Notification pop up  */

.pop-over.mod-notifications {
    background: #fff;
}
.thumbnail {
    background: #979798 !important
}
.sidebar-section-header-title {
    text-decoration: none !important;
    font-weight: 300 !important
}
#search-text {
    border-radius: 0px !important
}
/* Board Menu  */

.board-wrapper.is-show-menu .board-menu {
    background: #fff;
}
.js-menu-action-list {
    display: none;
}
.show-more {
    display: none;
}
.board-menu-navigation-item-link {
    font-weight: 300;
}
.board-menu-navigation-item-link:hover {
    background: #f9f9f9;
}
.board-menu-section-header-title {
    font-weight: 300;
}
.board-menu-section-header:hover {
    background: #f9f9f9;
}
.board-menu-navigation-item-link-count,
.board-menu-section-header-count {
    background: #f9f9f9;
}
.text {
    text-decoration: none !important;
}
.member {
    border-radius: 20px !important;
    border: none !important;
    background: none !important;
}
.member img {
    border-radius: 0 !important;
    border: none !important;
}
/* Text input  */

.iframe-input,
input[type=text],
input[type=password],
input[type=email],
textarea{
    background-color: #f7f7f6;
    border: 1px solid #c9c9c9;
    border-radius: 0px !important;
}
.iframe-input:hover,
input[type=text]:hover,
input[type=password]:hover,
input[type=email]:hover,
textarea:hover {
    border-color: #a5a5a5;
}
.iframe-input.focus,
input[type=text]:focus,
input[type=password]:focus,
input[type=email]:focus,
textarea:focus {
    border-color: #c9c9c9;
    box-shadow: none !important;
}
.iframe-input, button, input:not([type=file]), textarea {
    border-radius: 0px;
}
/* Lists  */

.list-header-name {
    color: #787878;
    font-weight: 100 !important;
}
.list-header {
    padding-top: 10px;
    padding-bottom: 10px;
}
.list {
    background: #fff !important;
    border: 1px solid #c9c9c9;
    border-radius: 0px;
}
.list-card {
    /*  background-color: #f9f9f9; */
    
    border-radius: 0px;
    border: 1px solid #f0f0f0;
    margin-bottom: 10px;
}
.list-card a,
.sticker-select-upload:hover .sticker-select-upload-icon {
    color: #999;
}
.list-card.active-card {
    background-color: #fff;
    border-right-color: #d6dadc;
    border-left-color: #d6dadc;
    border-top-color: #d6dadc;
}
.list-card:hover {
    transition: background 0.5s ease-out !important
}
.list-card-operation {
    background-color: #f9f9f9;
    top: 5px;
}
.list-card-operation:hover {
    background-color: #fffffd;
}
.list-card-details {
    padding: 0px;
}
.list-card-labels {
    margin: 0px 0px 2px 0px;
}
.card-label {
    border-radius: 0px;
    text-shadow: none;
}
.card-label.mod-card-front {
    float: none;
    height: 4px;
    width: 100%;
    z-index: -1;
}
.list-card-title {
    margin: 8px 6px 2px 10px;
}
.badges {
    margin: 0 6px 6px 10px;
}
.open-card-composer {
    text-decoration: none;
    background: #fffffd;
    color: #999;
    border-top: 1px solid #c9c9c9 !important;
    border-radius: 0px;
}
.open-card-composer:hover {
    background: #fff;
    transition: background 0.5s ease-out;
    text-decoration: none;
}
/* Labels */

.card-label-green {
    background-color: #adff99
}
.card-label-lime {
	background-color: #A456AD
}
.card-label-yellow {
    background-color: #ffec72
}
.card-label-orange {
    background-color: #ffda99
}
.card-label-red {
    background-color: #ff9c99
}
.card-label-purple {
    background-color: #F6A5FF
}
.card-label-blue {
    background-color: #009CBC
}
.card-label-sky {
    background-color: #5ed6ff
}
.card-label.mod-card-detail {
    border-radius: 0px;
}
.card-detail-badge {
    border-radius: 0px
}
/* Add a list */

.list-wrapper.mod-add.is-idle {
    background: none;
    border: 1px solid #fffffd;
    border-radius: 0px;
}
.list-wrapper.mod-add.is-idle:hover {
    border: 1px solid #c9c9c9;
    background: none;
}
.list-wrapper.mod-add {
    background: #fff;
    border-radius: 0px;
}
.list-wrapper.mod-add .placeholder {
    color: #999;
}
.list-wrapper.mod-add .list-name-input {
    background: #fff !important;
    border-color: #c9c9c9;
    box-shadow: none !important
}
.badge.is-complete {
    background-color: #adff99;
    border-radius: 0;
} 
/* List + Card buttons */

input[type=submit].primary {
    background: #c9c9c9;
    box-shadow: none;
}
input[type=submit].primary:focus,
input[type=submit].primary:hover {
    background: #787878;
}
/* Edit Card */

.window-wrapper {
    background: #fff !important;
}
.window {
    border-radius: 0px;
}
.window-wrapper a {
    text-decoration: none !important
}
.window-overlay {
    background: rgba(218, 218, 216, 0.8) !important;
}
.editing .edit .field, .editing .edit .field:active {
    background: none;
    border-color: #c9c9c9;
    box-shadow: none;
}
.checklist-item {
	border-radius: 0px;
    border: 1px solid #fff;
}
.checklist-progress-bar {
	border-radius: 0px;
}
/* Hiding everything that I don't need  */

.add-comment-section {
    display: none;
}
.window-module-title {
    display: none;
}
.card-detail-item-header {
    display: none;
}
.card-detail-item-header-edit {
    display: none;
}
.js-open-header-info-menu {
    display: none;
}
.js-open-header-notifications-menu {
    display: none;
}
/* strong {
    font-weight: 300 !important;
} */
.js-open-move-from-header {
    color: #c9c9c9 !important;
    font-weight: 300 !important
}
.window-title-text {
    font-weight: 300 !important
}
.button-link {
    font-weight: 300;
    background: #fff;
    border-radius: 0px;
    color: #999;
}
.button-link:hover {
    background: none;
    border-color: #c9c9c9 !important;
    color: #787878;
}
.button-link:hover .icon-sm {
    color: #787878 !important;
}
.checklist-progress-bar-current {
    background: #787878 !important
}
.ui-state-active {
    background: #c9c9c9 !important
}
}
