css:
@-moz-document domain(www.tumblr.com) {
 
/* I call this the "Get off the internet, Louise!" Theme */
 

/* The Background */
body {
background:#272727 url(http://68.media.tumblr.com/55c07e1f5a12993033a0d067aafd37c0/tumblr_inline_n25a6zYeib1qhwjx8.gif) repeat center fixed! important;
}

/* Content */
div.l-content.l-content.l-content {
background: rgba(255, 255, 255, 0.5) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
border: 2px solid #FFCCDD;
margin: auto;
padding: 20px 20px;

  }

/* The right and left columns */
#left_column, #right_column {background-color: transparent !important;}

/* The header */
.l-header-container--refresh {
   box-shadow: 0 0 0 1px hsla(0,0%,100%,.13);
   -webkit-transform: translateZ(0) translateY(0);
   transform: translateZ(0) translateY(0);
   background-color: rgba(255, 255, 255, 0.5);
   position: fixed;
   width: 100%;
   opacity: .7%;
   border: 1px solid #FFCCDD;
   color: #FFCCDD;

}

/* Also the posts and text */
.l-header-container--refresh .tab_notice, .new_post_notice_container, .post_full .post_wrapper, div.post_footer.clearfix, .post_full .post_body, .post.post_full, .post_full .post_tags:after, .right_column, .controls_section li, .identity .controls_section.user_list li .follow_list_item_blog:before, .follow.plus-follow-button:hover, .post.post_full .post_content, .post_full.is_note .post_body .note_item {
background: #FFCCDD !important;
border: 0px !important;
-webkit-border-radius: 5px;
-moz-border-radius: 5px;
border-radius: 5px;    
}

/* Circle Avatars */
.post_avatar {
  background-color: transparent !important;
}
.post_avatar_link, .post_sub_avatar {
  -webkit-border-radius:50% !important;
  -moz-border-radius:50% !important;
  border-radius: 50% !important;
  border: 2px solid #FFCCDD;
}

/* A cool cursor that actually functions. */
/* Start http://www.cursors-4u.com */
 body, a:hover {cursor: url(http://cur.cursors-4u.net/others/oth-9/oth800.cur), progress !important;} 
/* End http://www.cursors-4u.com */

/* The Post icons */
.icon_post_text:before,
.icon_post_photo:before,
.icon_post_quote:before,
.icon_post_link:before,
.icon_post_chat:before,
.icon_post_audio:before,
.icon_post_video:before {
color: white;    
}
 
/* To get the annoying blue stuff away */ 
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

#left_column, #right_column {
  background-color: transparent;
}
DIV.post_avatar {background-color: transparent}
  
}
