css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mastodon.social"), domain("awoo.space"), domain("witches.town"), domain("icosahedron.website"), domain("chitter.xyz"), domain("monsterpit.net"), domain("cybre.space"), domain("glitch.social"), domain("occult.camp"), domain("social.tchncs.de"){
	/* Reference */
    /*
    Main column container
    	Background colour: #e1e8ed
	Tweetdeck Tweet (non-faded)
    	Tweet link colour (mentions, hashtags, links): #2b7bb9
    	Tweet background colour: #fff
    	Tweet Display Name colour: #292f33
		Tweet handle name colour: #8899a6
	Tweetdeck Module header
    	Tweetdeck Module header background colour: #f5f8fa
    	Tweetdeck Module header border bottom colour: #e1e8ed
		Teetdeck Module header bold text colour: #292f33
		Tweetdeck Module header text line height: 50px
        Tweetdeck Module muted text colour: #8899a6
    Tweetdeck Tweet drawer
    	Drawer background colour: #2b7bb9
        Drawer button background colour: #3b94d9
        Drawer button background colour (focus): #1c6399
    */

	/* Main Settings */
	html{
    	/*font-family: "HelveticaNeue","Helvetica Neue",Helvetica,Arial,Verdana,sans-serif;*/
        font-family: "mastodon-font-sans-serif",sans-serif;
    	font-size: 14px;
    	font-weight: 400;
    	color: #292f33;
		line-height: 1.28578;
		text-rendering: optimizeLegibility;
  }
  	body, body.app-body{
    	height: 100%;
        background-color: #1c6399;
    }
    .columns-area{
    	background-color: #e1e8ed;
    }
    .scrollable, .column > .scrollable{
    	background-color: #f5f8fa;
    }
    
    /* Columns and Drawer */
    .drawer{
    	padding: 0 !important;
        margin: 0;
        background-color: #2b7bb9;
    }
    .drawer__header, .drawer__inner, .drawer .search{
    	padding: 0;
        margin: 0;
    	background: inherit;
    }
    .drawer .drawer__tab{
    	color: #fff;
        background: #2b7bb9;
        font-size: 24px;
    	padding-bottom: 10px;
        border-bottom: 1px solid #3b94d9;
        z-index: 2;
    }
    /*I hate adding focus since it gives the appearance of an element looking "stuck", but alas.*/
    .drawer .drawer__tab:hover, .drawer .drawer__tab:focus{
    	background: #3b94d9;
        box-shadow: 0 0 2px 3px #50a5e6;
        z-index: 10;
	}
    .drawer .drawer__tab:active{
    	background: #1c6399;
    }
    .drawer .search label input{
    	color: #777;
        background: #14171A;
    }
    .drawer .search .search__icon .fa{
    	color: #777;
    }
    .compose-form .compose-form__publish-button-wrapper .button{
    	background: #1da1f2;
        border: 1px solid #1da1f2;
        overflow: visible !important;
    }
    .compose-form .compose-form__publish-button-wrapper .button:hover{
    	background: #005fd1;
        border: 1px solid #005fd1;
        overflow: visible !important;
    }
    .compose-form .compose-form__publish-button-wrapper .button:focus{
    	border: 1px solid #1DA1F2;
        box-shadow: 0 0 0 1px #fff,0 0 0 3px #71C9F8;
        overflow: visible !important;
    }
    .compose-form .reply-indicator{
    	background: #e1e8ed;
    }
    .compose-form .reply-indicator .display-name__account{
    	color: #8899a6;
    }
    
    .getting-started__wrapper, .getting-started{
    	background: #2b7bb9;
    }
    .getting-started a{
    	color: #fff;
    }
    .column{
    	padding: 0;
        margin: 0 0 0 6px;
        /* Some elements (such as expanded statuses) won't have headers for buttons etc. This is to work around it to give the same visual appearance.*/
        background: #f5f8fa;
    }
    .column:last-child{
    	padding: 0;
    }
    .column-link{
        line-height:38px;
        min-height: 30px;
		margin: 2px 10px;
        padding: 4px 10px;
        background: #3b94d9;
        border-radius:4px;
    }
    .column-link:hover{
    	background: #3b94d9;
        box-shadow: 0 0 2px 3px #50a5e6;
    }
    .column-link:focus{
    	background: #3b94d9;
    	box-shadow: 0 0 2px 3px #50a5e6;
        border: 1px solid #1DA1F2;
    }
    .column-subheading{
    	color: #fff;
        border-bottom: 1px solid #3b94d9;
    	background: #2b7bb9;
    }
    
    /* Column Header Settings */
    .column-header{
    	color: #292f33;
    	line-height: 50px;
        height: 50px;
        padding: 0 10px;
        border-bottom: 1px solid #e1e8ed;
        background-color: #f5f8fa;
    }
    .column-header > button{
    	padding: 0;
    }
    .column-header__icon{
    	padding: 15px 0px 15px 5px;
        font-size: 22px;
        margin-right: 0 !important;
    }
    .column-header__title{
    	font-size: 22px;
        font-weight: normal;
    	line-height: 50px;
        height: 50px;
    }
    .column-header__buttons{
		margin: 0 -10px 0 0 !important;
        height: 50px;
    }
    .column-header__button{
        color: #aab8c2 !important;
        font-size: 22px;
    	position: relative;
    	background: none;
	}
	.column-header__button.active, .column-header__button.active:hover{
    	height: 51px;
        overflow: visible;
    	border-width: 1px;
        border-style: solid;
        border-color: #e1e8ed;
        border-bottom-color: #fff;
    	background: #fff !important;
	}
    .column-header__collapsible{
    	color: #8899a6;
	}
	.column-header__collapsible-inner{
    	background-color: #fff;
	}
    .column-settings__section{
    	border-bottom: 1px solid #e1e8ed;
    }
    .column-back-button{
        font-weight: normal;
        line-height: 50px;
    	height: 50px !important;
        padding: 0 0 0 15px;
        display: inline-block;
    	background: none;
    }
    .column-header__back-button{
    	background: none;
    }
    .column-back-button--slim .column-back-button{
    	/*Cheap fix ?*/
        margin:-3px 0 0 0;
        padding: 0 15px 0px 0;
    }
    
    /* Status Settings */
	.status{
		color: #292f33;
		border-bottom: 1px solid #e1e8ed
	}
    .status__wrapper, .status__prepend, .focusable{
    	background: #fff !important;
    }
    .status .display-name__html, strong.display-name__html{
		color: #292f33;
        box-sizing: border-box;
        position: relative;
        display: inline-block;
        max-width: 100%;
	}
    .status__display-name .display-name__account{
		color: #8899a6;
	}
    	/* Expanded Status Settings */
        .detailed-status__wrapper{
        	/* Some elements (such as expanded statuses) won't have headers for buttons etc. This is to work around it to give the same visual appearance.*/
        	border-top: 1px solid #e1e8ed;
        }
    	.detailed-status, .focusable:focus .detailed-status, .focusable:focus .detailed-status__action-bar{
    		color: #292f33;
        	font-size: 1.2857142857rem !important;
        	border-bottom: 1px solid #e1e8ed;
        	background-color: #fff !important;
    	}
    	.detailed-status .display-name__html{
    		color: #292f33;
    	}
    	.detailed-status .display-name__html strong:hover, .detailed-status .mention{
    		color: #2b7bb9;
    	}
        .detailed-status .display-name__account{
        	color: #8899a6;
        }
    	.detailed-status__action-bar, .focusable:focus .detailed-status__action-bar{
        	border-width: 0 0 1px 0;
            border-style: solid;
            border-color: #e1e8ed;
    		background: #f5f8fa !important;
    	}
    /* Notification Settings (Misc, Cleanup) */
    .notification .muted strong, .notification__display-name, .muted .mention{
    	color: #8899a6;
    }
    .notification__message, .notification__display-name{
    	color: #292f33;
    }
    .notification .account{
		border-bottom: 1px solid #e1e8ed;
    }
        
	/* Expanded Profile Settings */
     .account__header>div{
     	background: none;
     }
     .account__header{
     	text-shadow: 0 1px 1px rgba(17,17,17,0.8);
     }
     .account__header__display-name{
        font-size: 24px !important;
     }
     .account__header__username{
     	font-size: 18px;
		line-height: 24px;
		padding-top: 1px;
		display: block;
		color: #fff !important;
     }
    .account__header__content{
    	color: #fff !important;
		word-wrap: break-word;
		font-size: 14px;
		line-height: 18px;
		text-align: center;
    }
    .account__action-bar{
    	border-top: 1px solid #eaeaea;
        border-bottom: none;
    }
    .account__action-bar__tab{
    	border-left: 1px solid #eaeaea;
    }
    .account__action-bar__tab>span{
    	color: #8899a6;
    }
    .account__action-bar__tab>strong span{
    	color: #292F33;
    }
    .account__action-bar__tab:hover span{
    	color: #2b7bb9 !important;
    }
    
    /* Lists */
    .column-inline-form, .modal-root .search{
    	background: #f5f8fa;
    }
    .modal-root .search{
    	margin: 5px;
    }
    .setting-text, .modal-root .search__input{
    	color: #85888a;
        padding-left: 7px;
    }
    .setting-text:focus, .modal-root .search__input:focus{
    	color: #85888a;
        background: inherit;
    }
    .column-inline-form label input:focus, .modal-root .search label input:focus{
        box-shadow: inset 0 1px 3px rgba(17,17,17,0.1),0 0 8px rgba(80,165,230,0.6);
        border-radius: 4px;
        border-color:rgba(80,165,230,0.8)
    }
    .column-inline-form label input, .modal-root .search label input{
    	border: 1px solid #e1e8ed;
        border-radius: 4px;
        background: #f0f0f0;
    }
    .list-editor{
    	background: #f5f8fa;
    }
    .list-editor h4{
    	background: #fff;
        color: #292f33;
    }
    .list-editor .search__icon .fa{
    	color: #aab8c2;
    }
    .list-editor__accounts{
    	background: #fff;
    }
    .drawer__backdrop{
    	background: #fff;
    }
    .list-editor .drawer__inner.backdrop{
    	width: 100%;
        box-shadow: none;
        border-radius: 0;
    }
    .drawer__inner .account{
    	border-bottom: 1px solid #e1e8ed;
    }
    .drawer__inner .account .icon-button{
    	color: #292f33;
    }
    .drawer__inner .account .icon-button:focus, .drawer__inner .account .icon-button:hover{
    	color: #2b7bb9;
    }
    
    /* Misc columns */
    /* Resource 404 */
    .regeneration-indicator{
    	background: transparent;
		border-top: 1px solid #e1e8ed;
        color: #aab8c2;
    }
    .regeneration-indicator__label strong{
    	color: #aab8c2;
    }
    .empty-column-indicator{
    	background: #fff;
        color: #aab8c2;
    }
    
    /* Keyboard shortcuts column */
    .keyboard-shortcuts{
    	color: #292f33;
        background: #fff !important;
        padding: 0;
    }
    .keyboard-shortcuts table{
    	width: 100%;
    }
    .keyboard-shortcuts tr:nth-child(2n){
        background-color: #f5f8fa;
    }
    .keyboard-shortcuts td{
    	padding: 8px 10px;
    }
    .keyboard-shortcuts kbd{
    	background: #fff;
        color: #2b7bb9;
        border-radius: 2px;
        border: 1px solid #2b7bb9;
    }
    
    /* Common Links Settings */
    .status .mention, .status__relative-time:hover, .status-link,.account__display-name:hover strong, .column-header__button:hover,.detailed-status__display-name:hover strong, .reply-indicator__display-name:hover strong, .status__display-name:hover strong, .permalink:hover, .notification__display-name:hover, .status__content a, .reply-indicator__content a, .column-header__setting-btn:hover, .column-header__setting-btn:focus{
		color: #2b7bb9;
	}
    .status__content .status__content__spoiler-link{
    	background: #fff;
        color: #2b7bb9;
        border: 1px solid #2b7bb9;
    }
    .status__content .status__content__spoiler-link:hover{
    	color: #fff;
        background: #2b7bb9;
        border: 1px solid #2b7bb9;
    }
}
