css:
@-moz-document domain("yahoo.co.jp") {
/* common */

html {
  background-size: cover !important;
  background-attachment: fixed !important;
  background-position: center top !important;
  background-image: url('https://s24.postimg.org/6tetctkg5/tales_of_zestiria_art.jpg') !important;
  background-repeat: no-repeat;
}
body {
  background-color: rgba(255, 255, 255, 0.9) !important;
}
#clr, #SaA .gLnk .lnk a {
  display: none;
}
}

@-moz-document domain("yahoo.co.jp") {
/* colors */

/* search buttons */

#srchbtn, #pageTop, .searchForm .b {
  border-color: #fb0034 !important;
  background-color: #fb0034 !important;
  outline: none;
}
#srchbtn {
  background-image: url(https://k.yimg.jp/images/top/sp2/clr/161121/2.png) !important;
}
.searchForm .b {
  background-position: 25px -57px !important;
}
#srchbtn:hover, #pageTop:hover, .searchForm .b:hover {
  border-color: #c70028 !important;
  background-color: #c70028 !important;
}
a {
  color: #6b4b5c;
}

/* search.yahoo.co.jp */

#SaA {
  border-top-color: #efddd1 !important;
}
#SaA-tg-list strong {
  border-top-color: #c79e92 !important;
}
#WS2m .u, #WS2m .a .u a, .ss .u {
  color: #9e6db0 !important;
}
#header {
  background-color: #fff5e6 !important;
}
#Sf2 {
  border-bottom-color: #efddd1 !important;
}
#SaA-tg-list a:hover, #SaA-tg-list .open {
  background-color: #c79e92 !important;
}
}

@-moz-document domain("www.yahoo.co.jp") {
/* common */

@media (min-width: 1371px) {
  html {
    background-size: cover !important;
    background-attachment: fixed !important;
    background-position: center top !important;
    background-image: url('https://s24.postimg.org/6tetctkg5/tales_of_zestiria_art.jpg') !important;
    background-repeat: no-repeat;
  }
}
@media (max-width: 1370px) {
  html {
    background-size: auto !important;
    background-attachment: fixed !important;
    background-position: calc(50% + 155px) top, calc(50% - 90px) top !important;
    background-image: url('https://s24.postimg.org/6tetctkg5/tales_of_zestiria_art.jpg'), url('https://s24.postimg.org/6tetctkg5/tales_of_zestiria_art.jpg') !important;
    background-repeat: repeat-y;
  }
}
html body {
  background: none !important;
}
#header:before, #contents:before, #uhd:before {
  width: 970px;
  position: absolute;
  height: 100%;
  content: "";
  top: 0;
  left: -10px;
  background: #fff;
  z-index: -1;
}
#brandpanel {
  width: 350px !important;
}
}

@-moz-document domain("www.yahoo.co.jp") {
/* colors */

/* bg */

#navi .bx, .bgA2, .bgA3, .bgA4, .bgA5, .bgA6, #topicsboxbd .topicsdetail {
  background-color: #fff5e6 !important;
}
.bxShp, #ultra, #centralPosition, #tst {
  border-color: #c79e99 !important;
  background: #fff5e6 !important;
}

/* bg shadowed */

#topicsbox .hd .tab .next, #topicsbox .hd .tab .prev {
  background-color: #efddd1 !important;
}

/* bg active tab */

#topicsbox .tab .on span, #topicsbox .tab .tab4.on span {
  background-color: #fff !important;
}

/* borders */

.bx, .bdA1, .bdA2, .bdA3, .bdA4, .bdA6, .bdrA2, .bdrA4, .bdrB2, .bdB3, #contentbox .hd, #navi .hd, #topicsboxbd .topicsdetail, #topicsboxbd, #topicsbox .tab li.on, #topicsbox .on0 .tab0.on {
  border-color: #c79e99 !important;
}
.bdlB2 {
  border-left-color: #c79e99 !important;
}
#topicsbox .tab .on span, #topicsbox .tab .tab4.on span, #topicsbox .tab .off span, #topicsbox .tab .tab4.off span {
  border-right-color: #c79e99 !important;
}
.bdA5 {
  border-color: #fff5e6;
}

/* borders inactive */

.bdrA4, #topicsbox .tab .tab4 span {
  border-color: #ebdcda !important;
}

/* borders inner active */

#topicsbox .hd .tab .next, #topicsbox .hd .tab .next a, #topicsbox .hd .tab .prev, #topicsbox .hd .tab .prev a {
  border-color: #efddd1 !important;
}

/* bottom border active tab */

.Topics#topicsbox .tab .on span {
  border-bottom-color: #fff !important;
}

/* gradient */

#division .hd, #spotlight, #ftSrchbtn {
  background: linear-gradient(0, #fff, #fff5e6);
}

/* ninjo bubble fix */

.ninjyo__wrapperImage--leftTop {
  background-position: -20px -44px !important;
}
@media (-webkit-min-device-pixel-ratio: 2) {
  .ninjyo__wrapperImage--leftTop {
    background-position: -17px 1px !important;
  }
}
}
