css:
@-moz-document domain("telegra.ph") {
/* 基于 Telegraph - zh_CN v0.4 编写而来 */
.tl_article h1,
.tl_article h2,
.tl_article address,
.tl_article .tl_article_content h3,
.tl_article .tl_article_content h4,
.tl_article .tl_article_content figcaption,
.tl_article .tl_article_content figcaption .editable_input,
.tl_message .tl_tooltip .prompt_input .error_msg .tl_link_tooltip {
    font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", Helvetica, Arial, "PingFang SC", "Source Han Sans SC", "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.tl_article .tl_article_content,
.tl_article .tl_article_content .ql-editor,
.tl_article .tl_article_header,
.tl_article .tl_article_content blockquote
.tl_article .tl_article_content aside {
    font-family: Georgia, Cambria, 'Times New Roman', "PingFang SC", "Source Han Sans SC", "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

.tl_article .tl_article_content aside {
    text-align: inherit;
}

.tl_article .tl_article_content,
.tl_article .tl_article_content .ql-editor,
.tl_article .tl_article_header {
    line-height: 2;
}

.tl_article address {
    margin-top: 16px;
    margin-bottom: 22px;
}

.tl_article .tl_article_content blockquote
.tl_article .tl_article_content aside {
    font-style: normal;
}

.tl_article address time::before {
    content: "·";
}
.tl_article .tl_article_content blockquote {
    font-size: 1em;
    font-style: normal;
    padding: 30px 38px;
    margin: 0 0 15px;
    position: relative;
    line-height: 1.8;
    text-indent: 0;
    border: none;
    color: #888;
	text-align:center

}

.tl_article .tl_article_content blockquote:before {
    content: "“";
    left: 12px;
    top: 0;
    color: #E0E0E0;
    font-size: 4em;
    font-family: Arial, serif;
    line-height: 1em;
    font-weight: 700;
    position: absolute;

}

.tl_article .tl_article_content blockquote:after {
    content: "”";
    right: 12px;
    bottom: -26px;
    color: #E0E0E0;
    font-size: 4em;
    font-family: Arial, serif;
    line-height: 1em;
    font-weight: 700;
    position: absolute;
    bottom: -31px;
}

.tl_article .tl_article_content h3 {
    font-size: 1.4em;
    margin: 40px 10px 20px 0;
    padding-left: 9px;
    border-left: 6px solid #ff7e79;
    font-weight: 700;
    line-height: 1.4;
}

.tl_article .tl_article_content h4 {
    font-weight: 700;
    text-transform: uppercase;
    font-size: 1.1em;
    line-height: 1.4;
    margin: 10px 0 5px;
    padding-top: 10px
}


.tl_article .tl_article_content a {
    color: #f55852;
    text-decoration: none;
	border-bottom: none
}
.tl_article .tl_article_content a:hover {
    color: #f55852;
    text-decoration: underline;
}
.tl_article .tl_article_content a:focus {
    outline-offset: -1px;
}
}
