@namespace url(http://www.w3.org/1999/xhtml);
/*-----[ VK Image Zoom BY sSolov ]-----*/

@-moz-document domain("vk.com") {
  /*-- BodyStyle --*/
/*[[BodyStyle]]*/
  /*-- LogoX --*/
/*[[LogoX]]*/

  /*-- Left-Menu --*/
/*[[LeftMenu]]*/

  /*-- Comments --*/
/*[[Comments]]*/

  /*-- Image --*/
/*[[Image]]*/

  /*-- Image-Center --*/
  .pv_cont .pv_left_wrap{
    width:100%;
  }
  .pv_cont .pv_img_area_wrap #pv_photo{
    width: 100% !important;
    height: 100% !important;
  }
  .pv_cont .pv_close_btn {
    right: 20px !important;
  }
  #ads_left{ /* убрать блок рекламы */
    display: none !important;
  }
}

style_setting_options.value:
  #page_header_cont .back{ /* header-content */
    background-color: rgba(11,11,11, 0.8);
    background-size:50px;
  }
  .page_block_header{
    opacity:0.75;
  }
  .page_block .ui_search.ui_search_fixed{
    opacity:0.5;
  }
  .page_block .ui_search.ui_search_fixed:hover{
    opacity:1;
  }
  .ui_search{
    opacity:0.5;
  }
  .ui_search:hover{
    opacity:1;
  }
  input.text.ts_input{
    background-color: #EDEEF0;
  }
  .static_header #page_header_cont{
    position: fixed;
  }
  .top_nav_btn:hover,.top_audio_player:hover,.top_nav_link:hover{
    background-color: rgba(72, 105, 145, 0.6)
  }

  /* serch-form */
  .input_back_wrap .input_back, .ts_wrap .input_back_content{
    display: none;
  }
  input.text.ts_input{
    opacity: 0.5;
    padding: 0 0 0 19px;
    width:30px;
    /* slow-animation */
    -webkit-transition: 0.2s;
       -moz-transition: 0.2s;
         -o-transition: 0.2s;    
            transition: 0.2s;
  }
  input.text.ts_input:hover, input.text.ts_input:focus{
    opacity: 1;
    width:230px;
    /* slow-animation */
    -webkit-transition: 0.3s;
       -moz-transition: 0.3s;
         -o-transition: 0.3s;
            transition: 0.3s;
  }

  .pv_cont .pv_img_area_wrap #pv_photo img{
    margin-top: 0px !important;
    /* slow-animation */
    -webkit-transition-duration: 0.2s;
       -moz-transition-duration: 0.2s;
         -o-transition-duration: 0.2s;    
            transition-duration: 0.2s;
  }
  .pv_cont .pv_img_area_wrap #pv_photo img:hover{ /* zoom image */
    width: 100% !important;
    height: 100% !important;
    /* slow-animation */
    -webkit-transition-duration: 0.4s;
       -moz-transition-duration: 0.4s;
         -o-transition-duration: 0.4s;    
            transition-duration: 0.4s;
    -webkit-transition-delay: 0.3s;
       -moz-transition-delay: 0.3s;
         -o-transition-delay: 0.3s;    
            transition-delay: 0.3s;
  }

  /*-- zoom image FullScreen --*/
  #pv_fullscreen{
    overflow: auto;
  }
  #pv_fullscreen #pv_fs_img_wrap img{
    -webkit-transition-duration: 0.2s;
       -moz-transition-duration: 0.2s;
         -o-transition-duration: 0.2s;    
            transition-duration: 0.2s;
  } 
  #pv_fullscreen #pv_fs_img_wrap img:hover{
    height: 100% !important;
    width: 100% !important;    
    -webkit-transition-duration: 0.4s;
       -moz-transition-duration: 0.4s;
         -o-transition-duration: 0.4s;    
            transition-duration: 0.4s;
    -webkit-transition-delay: 0.3s;
       -moz-transition-delay: 0.3s;
         -o-transition-delay: 0.3s;    
            transition-delay: 0.3s;
  }

.pv_cont .pv_narrow_column_wrap{ /* comments */
  width:100%;
  height: 100%;
  opacity:0.10;
  position:absolute;
  margin-left: -100%;
  clip: rect(0, 66px, 60px, 0);
}
.pv_cont .pv_narrow_column_wrap:hover{ /* comments: hover */
  opacity:1;
  clip: initial;
}
#pv_narrow, #pv_reply_form_wrap{
  width:100% !important;
}
.pv_reply_form_wrap .page_add_media{
  padding-right: 20px;
  margin-top: -45px;
  right: 0px;
  position: absolute;
}

.pv_cont .pv_narrow_column_wrap{ /* comments -right */
  opacity: 0.6;
  position: absolute;
  -webkit-transition-duration: 0.2s;
     -moz-transition-duration: 0.2s;
       -o-transition-duration: 0.2s;
          transition-duration: 0.2s;
  -webkit-transition-delay: 0.2s;
     -moz-transition-delay: 0.2s;
       -o-transition-delay: 0.2s;
          transition-delay: 0.2s;
}
.pv_cont .pv_narrow_column_wrap:hover{ /* comments: hover */
  margin-left: -280px;
  opacity: 1;
  -webkit-transition-duration: 0.3s;
     -moz-transition-duration: 0.3s;
       -o-transition-duration: 0.3s;
          transition-duration: 0.3s;
  -webkit-transition-delay: 0.2s;
     -moz-transition-delay: 0.2s;
       -o-transition-delay: 0.2s;
          transition-delay: 0.2s;
}

.pv_cont .pv_narrow_column_wrap { /* comments -right */
  opacity: 0.6;
  width: 100%;
}
.pv_cont .pv_narrow_column_wrap:hover { /* comments hover */
  opacity: 1;
}
.pv_cont .pv_reply_form_wrap {
  width: 100%;
}
#pv_narrow {
  max-height: 450px;
  height: 100% !important;
  width: 100% !important;
}
.pv_no_commments_placeholder_wrap {
  margin-top: 20px !important;
}
#layer, .pv_light #layer {
  margin-top: 2px !important;
}

.side_bar_inner { /* fixed menu */
  position: fixed !important;
  top: 0 !important;
  margin-top: 43px !important;
}
.side_bar_inner{
  width:40px;
  z-index: 196;
}
#page_header{
  width: 840px !important;
}
#page_layout{
  width: 840px !important;
}
#page_layout #page_body{
  z-index: 100;
}
#page_layout #side_bar{
  z-index: 150;
}
#side_bar ol{
  margin:0px;
}
#side_bar ol li{
  margin-left: -3px;
}
#side_bar ol li .left_row:hover{
  background: rgba(225, 229, 235, 0.6);
}
#side_bar:hover .side_bar_inner{
  width:140px;
  -webkit-transition-delay: 0.3s;
  -moz-transition-delay: 0.3s;
  -o-transition-delay: 0.3s;
  transition-delay: 0.4s;
  transition-duration: .2s;
}
#side_bar .left_label{
  visibility: collapse;
}
#side_bar .more_div{
  width: 16px;
  margin-left: 5px;
}
#side_bar .left_fixer:hover .left_icon + .left_label{ /* показывать подписи */
  visibility: visible;
}
#side_bar .left_count_wrap{ /* счетчик-counter */
  margin-bottom: -30px !important;
  border-radius: 0px;
}
/* other-links */
.left_menu_nav_wrap{
  width: 0px;
  padding: 0px;
}
.left_menu_nav_wrap > a{ 
  display: none;
  margin-left: -50px;
  width: 100px;
}
.left_menu_nav_wrap:before {
  content:"_______";
  color: #dfe2e8;
}
.left_menu_nav_wrap:hover a {
    display:block;
}

