css:
@-moz-document url("https://app.roll20.net/editor/") {
body {
background:url(http://subtlepatterns.com/patterns/dark_leather.png)!important;
}

#floatingtoolbar {
    background-color:hsl(0,0%,20%) !important;
    border: none !important;
    box-shadow: none !important;
    width: 40px !important;
}

#floatingtoolbar div.submenu {
    left: 40px !important;
    padding: 0 !important;
    top: 0 !important;
}

#floatingtoolbar div.submenu .pictos, #floatingtoolbar div.submenu .pictosthree, #floatingtoolbar div.submenu .pictostwo {
    left: 15px !important;
    position: absolute !important;
    text-align: center !important;
    width: 20px !important;
}

#floatingtoolbar div.submenu ul {
    border: 0 !important;
    border-radius: 0 !important;
    padding: 0 !important;
    width: 190px !important;
}

#floatingtoolbar div.submenu ul li {
    color:hsl(0,0%,10%) !important;
    padding-left: 45px !important;
    padding-right: 20px !important;
}

#floatingtoolbar div.submenu ul li span {
    padding: 0 !important;
}

#floatingtoolbar div.submenu ul li:hover {
    background-color:hsl(195,70%,76%) !important;
}

#floatingtoolbar li {
    border: 0 !important;
    color:hsl(0,0%,80%) !important;
    height: 50px !important;
    line-height: 50px !important;
    padding: 0 !important;
}

#floatingtoolbar li.activebutton, #floatingtoolbar li:hover {
    background-color:hsl(360,100%,100%) !important;
    color:hsl(0,0%,7%) !important;
}

#floatingtoolbar li:last-child {
    border-bottom: none !important;
}

#floatingtoolbar ul {
    box-shadow: none !important;
}

#page-toolbar {
    background-color:hsl(0,0%,20%) !important; 
    border-radius: 0 !important;
    height: 160px !important;
    left: 0 !important;
    right: 0 !important;
    width: auto !important;
}

#page-toolbar .availablepage {
    box-sizing: border-box !important;
    height: 160px !important;
    margin: 0 !important;
    padding: 10px !important;
    vertical-align: top !important;
    width: 160px !important;
}

#page-toolbar .availablepage .pagethumb {
    margin-top: 5px !important;
}

#page-toolbar .availablepage span {
    bottom: 30px !important;
    left: 0 !important;
    right: 0 !important;
    text-shadow: none !important;
    color:hsl(0,0%,80%) !important;
    font-size: 12px
 !important;
}

#page-toolbar .container {
    transition: opacity .4s ease-in-out !important;
}

#page-toolbar .handle {
    background:hsl(0,0%,20%) !important;
	border:1px solid hsl(0,0%,0%)!important;
    border-radius: 0 0 100% 100% !important;
    bottom: -29px !important;
    color:hsl(0,0%,80%) !important;
    height: 29px !important;
    opacity: 1 !important;
    width: 75px !important;
    right: 50% !important;
    margin-left: -50px !important;
    padding: 0 !important;
}

#page-toolbar .handle .pictos {
    display: none !important;
}

#page-toolbar .handle:after {
    content: 'PAGES' !important;
    font-size: 13px !important;
    letter-spacing: 2px !important;
    height: 100% !important;
    left: 0 !important;
    position: absolute !important;
    top: 0 !important;
    width: 100% !important;
}

#page-toolbar.closed .container {
    box-shadow: 0 0 50px rgba(0,0,0,.15) !important;
    opacity: 0 !important;
}

#editor-wrapper {
	overflow:hidden!important;
}

.ui-widget-content {
	color:hsl(0,0%,75%)!important;
	border:1px solid black!important;
	background:hsl(0,0%,20%)!important;
}

.ui-dialog-titlebar-close {
	background:hsl(0,0%,80%)!important;
}

#rightsidebar {
	background-color:hsl(0,0%,0%)!important;
}

#sidebarcontrol {
    opacity: 1 !important;
    padding: 0 !important;
    -webkit-transform: translateX(0) rotate(-90deg) !important;
    -moz-transform: translateX(0) rotate(-90deg) !important;
    width: 75px !important;
    text-align: center !important;
    border:1px solid hsl(0,0%,0%)!important;
    font-size: 13px !important;
    color:hsl(0,0%,80%) !important;
    background-color:hsl(0,0%,20%) !important;
    height: 28px !important;
    line-height: 35px !important;
    text-transform: uppercase !important;
    letter-spacing: 2px !important;
    -webkit-transform-origin: 100% 100% !important;
    -moz-transform-origin: 100% 100% !important;
    border-radius: 100% 100% 0 0 !important;
    top: 50% !important;
    margin-top: -75px !important;
}

#sidebarcontrol .fonticon-menu {
    display: none !important;
}

#sidebarcontrol:after {
    content: 'Menu' !important;
}

