css:
@-moz-document domain("animespirit.ru"), domain("animespirit.cc"), domain("animespirit.online") {
div.accordion p embed {
    width: 1000px;
    height: 615px;
    position: relative;
    z-index: 900;
}

div.accordion p object {
    width: 1000px;
    height: 615px;
    position: relative;
    z-index: 900;
}

div.accordion p iframe {
    width: 1000px;
    height: 615px;
}

div.content-main-block {
    background: #414141;
}

body {
    background: #414141;
}

.CollapsiblePanelContent, .content-block {
        background: #333;
        color: #fff;
    }

div.block {
    background: #444;
    border-radius: 5px;
}

.block-title {
    background-image: none;
    border-radius: 5px 5px 0 0;
	background-color:#600000;
}

.title_quote{
    border-radius: 5px 5px 0 0;
	background:#555;
    border-left: 4px solid #333;
}

.block-title h3 {
    background-image: none;
}

.block-title h2 {
    background-image: none;
}

.search-block {
    background-image: none;
    border-radius: 5px;
    background-color:#600000;
    box-shadow: 0 0px 10px#600000;
}

.search-block-in {
    background-image: none;
}

.content-block-title {
    background-image: none;
    background-color: #111;
    border-radius: 5px 5px 0 0;
    box-shadow: 0 0px 10px #111;
    margin-bottom: 5px;
}

.content-block-title h2 {
    background-image: none;
}

a {
    color: #557aff;
}

.accordion p {
    background: #222;
    border-left: solid 1px #555;
    border-right: solid 1px #555;
}

.accordion h3 {
    background: #222;
    border-color: #555;
}

.accordion h3:hover {
    background-color: #600000;
    box-shadow: 0 0px 10px #600000;
}

span[style="color:#000099"] {
    color: #6da8ba;
}

span[style="color:#CC0000"] {
    color: #c66;
}

span[style="color:#FF0000"] {
    color: #c66;
}

span[style="color:#FF0000"] {
    color: #c66;
}


div[id="loadingDiv"] {
  background-color:#333;
}

div[id="adminPanel"] {
    background-color:#333;
}

div[id="lastReleases"] {
    background-color:#7c7c7c;
}

div[id="lowSeedList"] {
    background-color:black; min-height:100px;
}

table[style="font-size:10px; background-color:rgb(240,245,250); padding:7px; border-width:1px; border-color:rgb(194,207,223); border-style:solid;"] {
    background-color:#999;
}

div[class="formsubtitle"] {
    background-color:#999;
}


div[class="row1"] {
    background-color:#999;
}

td[class="row1"] {
    background-color:#999;
}

td[class="row2"] {
    background-color:#999;
}

td[class="post2"] {
    background-color:#999;
}

td[class="catend"] {
    background-color:#999;
}

span[class="forumdesc"] {
    color:#000;
}

td[class="formbuttonrow"] {
    background-color:#666;
}

div[class="an_alert"] {
    background-color:#525252;
    border-color:#414141;
    color:#fff;
}

div[class="navigation"] {
    background-color:#414141;
}

div[class="unterline"], div[class="hr_line"] {
    display: none;
}

div[style="padding-top:5px;padding-bottom:2px;"]{
    background-color:#525252;
    border-color:#414141;
    color:#fff;
}

td[style="padding-top:12px; padding-left:13px; padding-right:13px;"]{
    background-color:#414141;
    border-color:#414141;
    color:#fff;
}
td[style="padding:5px;"]{
    background-color:#414141;
    border-color:#414141;
    color:#fff;
}

img[src="engine/skins/images/p1.gif"], img[src="engine/skins/images/pset.png"], img[src="engine/skins/images/next.png"]{
        display: none;
    }


td[style="padding-left:10px;"]{
    background-color:#525252;
    border-color:#414141;
    color:#fff;
}
html[class="gr__animespirit_ru"]{
    background-color:#414141;
}

td[bgcolor="#EFEFEF"]{
    background-color:#414141;
    border-color:#414141;
    color:#fff;
}

font[color="#CC0000"] {
    color: #c66;
}

font[color="#FF0000"] {
    color: #c66;
}

font[color="#990000"] {
    color: #c66;
}

div.accordion {
    box-shadow: 0 0px 20px #222;
}

.title_spoiler, .text_spoiler {
        background: #555;
        border: 1px solid #333;
    }

textarea[name="xfield[ost]"], textarea[name="full_story"], textarea[name="short_story"]{
    background: none 0% 0% / auto repeat scroll padding-box border-box #333;
    border: 1px solid #555;
    color: #fff;
	font-weight: normal;
}

input[type="text"], input[type="submit"], input[type="button"],
input[type="reset"] {
        background: #333;
        border: 1px solid #555;
        color: #fff;
        font-weight: normal;
    }
input[type="submit"]:hover, input[type="button"]:hover,
input[type="reset"]:hover {
        background: #444;
        border: 1px solid #555;
        cursor: pointer;
    }

fieldset {
        border: 1px solid aliceblue;
    }

legend {
        padding: 0 5px;
        color: aliceblue;
    }

fieldset ul {
        margin-bottom: 5px;
    }

fieldset ul li {
        list-style-type: square;
        margin-left: 10px;
    }

#nav li:hover ul.sub,
#nav li:hover ul.sub li {
    width: 120px;
}
#nav ul {
    box-shadow: 0 0 10px #000;
}
#nav li:hover ul.sub li a {
    width: 118px;
}

#nav li ul.sub li a.fly,
#nav li:hover li:hover a.fly,
#nav li:hover li:hover li:hover a.fly,
#nav li:hover li:hover li:hover li:hover a.fly,
#nav li:hover li:hover li:hover li:hover li:hover a.fly {
    background-position: 110px 6px;
}
#nav li:hover li:hover ul,
#nav li:hover li:hover li:hover ul,
#nav li:hover li:hover li:hover li:hover ul,
#nav li:hover li:hover li:hover li:hover li:hover ul {
    left: 110px;
    width: 120px;
}


#content:before {
         display: block;
         width: 100%;
         background: #555;
         content: "Anime Spirit";
         color: #FF8C00;
         font: bold 45px/80px 'Dancing Script', "Cooper", sans-serif !important;
         text-align: center;
    }

    #content:after {
         content: "Copyright © 2018 - AnimeSpirit.Ru.";
         text-align: center;
         width: 100%;
         display: block;
         padding: 0 0 33px 0;
		color: #FF8C00;
    }

#header, #footer,
#CollapsiblePanel_TopAnime,
#CollapsiblePanel_RTBanner_NOHIDE,
#CollapsiblePanel_RBBanner_NOHIDE,
#CollapsiblePanel_LTBanner_NOHIDE,
#CollapsiblePanel_LBBanner_NOHIDE,
#CollapsiblePanel_Recommend,
#CollapsiblePanel_AnimeRadio,
#CollapsiblePanel_Statistics,
#randomReleaseBlock
    { 
        display: none;
    }

td[style*="padding-left:5px; background:#ffffff"],
td[width="110"] { 
         background: #333 !important;
    }

table[width="95%"] tr:first-child td {
        padding: 11px 0;
    }

.ntitle {
        color: #0f0;
        font-size: 13px;
        padding: 5px 0;
    }

.title_spoiler a {
        color: #0f0;
    }

.quote {
        color: #0f0;
        background: #222;
    	border-left: 4px solid #333;
    }

.unit-rating, .current-rating, .unit-rating li a:hover {
        background-image: url('data:image/gif;base64,') !important;
    }

.search-block input {
        height: 25px;
        box-sizing:border-box;
        padding: 0 5px;
        font-size: 12px !important;
    }

.search-block h2 {
        display:none;
    }

.search-block form {
        padding: 9px 0;
    }
.navigation a {
        background: #333 !important;
        border: 1px solid #555 !important;
        color: #0f0 !important;
    }

.navigation a:hover, .navigation span {
        background: #444 !important;
        border: 1px solid #555 !important;
    }
td.a_block_82, .a_block_22 td:first-child, td[width="10"], 
    img[src*="/templates/Anime/images/dlet_"], 
    img[src*="/templates/Anime/images/spacer"]
    {
        display: none;
    }

td.a_block_22 { 
        color: #0f0 !important;
        background : #333 !important;
        padding-left: 15px;
    }

td.news { 
        color: #0f0 !important;
        background : #444 !important;
    }


td.slink {
        color: #0f0 !important;
        background : #333 !important;
        padding-left: 15px;
        padding-bottom: 5px;
    }

td.stext {
        color: #0f0 !important;
        background : #333 !important;
        padding-left: 15px;
    }

td.ctitle {
        background : #333 !important;
        color: #0f0;
        padding-top: 5px;
    }

td[width="140"] {
        padding: 10px 0 10px 15px;
         background: #333 !important;
         color: #fff !important;
    }

td.news {
        background: #333 !important;
         color: #fff !important;
         padding: 10px 15px;
    }

img[src*="/engine/data/emoticons/"] {
        max-width: 32px;
    }
span[style="color:#3333FF"], span[style="color:#000099"], 
span[style="color:#000066"], span[style="color:#333399"],
span[style="color:#3366CC"] {
         color: #3498db !important;
}

span[style="color:#CC0000"], span[style="color:#990000"],
span[style="color:#FF0000"],span[style="color:#663333"],
span[style="color:#993300"], span[style="color:#660000"],
font[color="#990000"], font[color="#CC0000"] {
         color: #e74c3c !important; 
}

span[style="color:#993399"], span[style="color:#663366"] {
         color: #9b59b6 !important;
}

span[style="color:#006600"] {
         color: #2ecc71 !important;
}

span[style="color:#000000"], .text_spoiler {
         color: #fff !important;
}

a[class="list"]{
         color: #0b0 !important;
}
}
