body::after {
	position: absolute;
    width: 0;
    height: 0;
    overflow: hidden;
    z-index: -1;
    content: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_lightbulb_outline_white_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_lightbulb_outline_black_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_check_circle_black_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_panorama_fish_eye_black_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_add_circle_white_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_remove_circle_white_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_format_color_text_black_24px.svg) url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_format_size_black_24px.svg);
