/* ---------------------------------------------------------------------- */
/* ------------------------------- SHARED ------------------------------- */
/* ---------------------------------------------------------------------- */

/* ripple */
.pagination ul>li>a,
#pagination li a,
.markItUpHeader ul ul a {
	position: relative;
	overflow: hidden;
}
.pagination ul>li>a::after,
#pagination li a::after,
.markItUpHeader ul ul a::after {
  	content: "";
  	display: block;
  	position: absolute;
  	width: 100%;
	height: 100%;
	top: 0;
	left: 0;
  	pointer-events: none;
  	background-image: radial-gradient(circle, #06b 10%, transparent 10.01%);
  	background-repeat: no-repeat;
  	background-position: 50%;
  	transform: scale(10, 10);
  	opacity: 0;
  	transition: transform .5s, opacity 1s;
}
/* colour alternatives */
.markItUpHeader ul ul a::after {
  	background-image: radial-gradient(circle, #000 10%, transparent 10.01%) !important;
}
.pagination ul>li>a:active::after,
#pagination li a:active::after,
.markItUpHeader ul ul a:active::after {
  	transform: scale(0, 0);
  	opacity: .2;
  	transition: 0s;
}

/* waves-effect */
#pagination li,
.button,
a.imgbutton {
	position: relative;
    cursor: pointer;
    display: inline-block;
    overflow: hidden;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-tap-highlight-color: transparent;
    vertical-align: middle;
    z-index: 1;
    will-change: opacity, transform;
    transition: all .3s ease-out;
}

