/*--------------------------------------------------------------------------------
  Star Button - Blue Bordered
  --------------------------------------------------------------------------------
  Author: Louis H - Tested on: Windows XP Firefox 3.0.5 - Last Update: 9.1.2009
  --------------------------------------------------------------------------------
  Description:
    Replace the star button with a blue bordered one
  --------------------------------------------------------------------------------*/


@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#star-button {
  padding: 1px !important;
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0px, 16px, 16px, 0px) !important;  
}

#star-button:hover {
  -moz-image-region: rect(0px, 32px, 16px, 16px) !important;  
}

#star-button:hover:active {
  -moz-image-region: rect(0px, 48px, 16px, 32px) !important;  
}

#star-button[starred="true"], .ac-type-icon.ac-result-type-bookmark {
  list-style-image: url("data:image/png;base64,") !important;  
  -moz-image-region: rect(0px, 16px, 16px, 0px) !important;  
}

#star-button:hover[starred="true"] {
  -moz-image-region: rect(0px, 32px, 16px, 16px) !important;  
}

#star-button:hover:active[starred="true"] {
  -moz-image-region: rect(0px, 48px, 16px, 32px) !important;  
}

#editBookmarkPanelStarIcon {
/*  list-style-image: url("chrome://browser/skin/places/starred48.png") !important; */
  list-style-image: url("data:image/png;base64,") !important;
  width: 48px !important;
  height: 48px !important;
}
