/*--------------------------------------------------------------------
  Bookmark & Identity Panels - TransDarkgraphite
  --------------------------------------------------------------------
  Author: Louis H - Tested on: Firefox 3.0.5 - Last Update: 9.1.2009
  --------------------------------------------------------------------
  Description:
    Make Bookmark & Identity Panels' background translucent with dark-graphite color
  --------------------------------------------------------------------
  Request:
    Restart Firefox to apply the style
  --------------------------------------------------------------------*/


@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* ======= IDENTITY PANEL ======= */
/* Popup Body Text */
#identity-popup-content-box > description,
#identity-popup-encryption-label {
  white-space: pre-wrap !important;
  color: #ffffff !important;
  -moz-padding-start: 15px !important;
  margin: 2px 0 4px !important;
}

#identity-popup-content-box > label {
  white-space: pre-wrap !important;
  -moz-padding-start: 15px !important;
  margin: 0 !important;
}

#identity-popup-content-host ,
#identity-popup-content-owner {
  font-weight: bold !important;
  max-width: 300px !important;
}

#identity-popup-content-host ,
#identity-popup-content-box.verifiedIdentity > #identity-popup-content-owner {
  font-size: 140% !important;
}

#identity-popup-content-owner {
  margin-bottom: 0 !important;
}

#identity-popup-content-verifier {
  margin: 4px 0 2px !important;
}

/* Popup Bounding Box */
#identity-popup {
  -moz-appearance: none !important;
  background: rgba(68,68,68,0.9)
  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACEAAAABCAYAAABQSMdZAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAgY0hSTQAAeiYAAICEAAD6AAAAgOgAAHUwAADqYAAAOpgAABdwnLpRPAAAAAlwSFlzAAAbvAAAG7wBureguwAAACVJREFUGFdj/PfvXz4DA8NvJPwLjQ+S+0MA/yWkhomJCaQGKwAAPAUdAvlFh7QAAAAASUVORK5CYII=") repeat-y !important;
  border: 1px solid rgba(255,255,255,0.15) !important;
  -moz-border-radius: 8px !important;
  -moz-border-radius-topleft: 0px !important;
  padding: 10px 8px 6px 8px !important;
  margin-top: 2px !important;
  color:  #ffffff !important;
  min-width: 280px !important;
}

#identity-popup-container {
  -moz-appearance: none !important;
  color:  #ffffff !important;
}


/* ======= BOOKMARKING PANEL ======= */
#editBookmarkPanelStarIcon[unstarred] {
  list-style-image: url("chrome://browser/skin/places/unstarred48.png") !important;
}

#editBookmarkPanel{
  -moz-appearance: none !important;
  background: rgba(68,68,68,0.9) 
  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACEAAAABCAYAAABQSMdZAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAbvAAAG7wBureguwAAABh0RVh0U29mdHdhcmUAQWRvYmUgRmlyZXdvcmtzT7MfTgAAAB1JREFUCJlj/P//PwMewMzAwMBCABOjhhUNsyHzAZ0nA27hA3ETAAAAAElFTkSuQmCC") repeat-y 100% !important;
  border: 1px solid rgba(255,255,255,0.15) !important;
  -moz-border-radius: 8px !important;
  -moz-border-radius-topright: 0px !important ;
  padding: 10px 8px 6px 8px !important;
  margin-top: 4px !important;
  margin-right: 20px !important;
  color: #ffffff !important;
}

#editBookmarkPanelTitle {
  font-size: 130% !important;
  font-weight: bold !important;
  margin: 8px 12px 0px 9px !important;
}

#editBookmarkPanel > hbox {
  -moz-padding-end: 1px !important;
}
