css:
@document url(http://juniorweb.ostisistemiweb.it),
url-prefix(http://juniorweb.ostisistemiweb.it)
{
/*sfondo*/
body {background-image: url("http://i.imgur.com/N5uANMO.jpg"); color:white;}

/*pannelli*/
div.panel.panel-default {background-image: url("http://i.imgur.com/gBJNVwN.jpg"); background-color:black;}
div.panel-heading {background-image: url("http://i.imgur.com/6G0bg8Z.jpg"); color:white !important;}

/*bottoni dropdown*/
button.btn.btn-default.btn-lg {background-image: url("http://i.imgur.com/6G0bg8Z.jpg"); color:white !important}
div.col-sm-7 {background-color:transparent;}
div.alert.alert-warning.text-center  {background-image: url("http://i.imgur.com/huugM3a.jpg"); color:white !important}
button.btn.dropdown-toggle.selectpicker.btn-default {background-image: url("http://i.imgur.com/6G0bg8Z.jpg");color:white !important}
button.btn.btn-default {background-color: darkblue;color:white !important}

/*box*/
select.form-control{background-image: url("http://i.imgur.com/6G0bg8Z.jpg");}
select.form-control:checked {color:white !important;}
select.form-control:root{color:white !important;}
input.form-control{background-color: lightgrey;}
bs-searchbox {background-color: lightGray !important}
select.form-control {color:white; background-color: lightgray;}

/*tabelle*/
tr {background-image: url("http://i.imgur.com/6G0bg8Z.jpg") !important;}
tbody {background-color: lightGray !important;}
td {background-image: url("http://i.imgur.com/DQrYSUE.jpg") }
td.highlightme {color:white;background-color:blue}

/*calendario*/
datepicker.datepicker-dropdown.dropdown-menu.datepicker-orient-left.datepicker-orient-top {background-image: url("http://i.imgur.com/od7Y5VL.png");}
span.month {background-image: url("http://i.imgur.com/od7Y5VL.png");}
th.datepicker-switch {color:white}
th.next {color:white}
th.prev {color:white}
td.day {color:white}
th.dow  {color:white}
td.new.day {color:gray !important}
td.old.day {color:gray !important}
td.day:hover {background-color:CornflowerBlue !important}

/*evidenzia riga tabelle*/
.table tr:hover td {background:DarkGray !important}

/*evidenzia oggetti menu*/
a:hover {background:CornflowerBlue !important}

/*vari*/
legend {color:white}
option {color:black; background-color:lightgrey}
option {color:black; background-color:lightgrey}
li {background-color:lightgrey}
ul.dropdown-menu {background-color:lightgrey}
div.col-md-2 {color:lightblue}

}
